import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 台中 -> 景點 -> 台中國家歌劇院","description":"","frontmatter":{"title":"台中國家歌劇院"},"headers":[],"relativePath":"台灣/台中/景點/台中國家歌劇院.md","filePath":"docs/台灣/台中/景點/台中國家歌劇院.md","lastUpdated":1766811211000}'),h={name:"台灣/台中/景點/台中國家歌劇院.md"};function m(c,a,p,g,_,f){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/台中/景點/台中國家歌劇院.md for this page in Markdown format</div><h1 id="台中國家歌劇院" tabindex="-1">台中國家歌劇院 <a class="header-anchor" href="#台中國家歌劇院" aria-label="Permalink to “台中國家歌劇院”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/b/bf/National_Taichung_Theater_2019.jpg" alt="台中國家歌劇院" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>台中國家歌劇院是由日本建築師伊東豊雄設計，以其獨特的「美聲涵洞」曲牆建築聞名，被譽為「世界最難蓋的建築」之一。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>美聲涵洞</strong>：內部由 58 面曲牆和 29 個洞窟組成，幾乎沒有任何樑柱與直角，呈現自然洞窟般的聲學效果。</li><li><strong>建築藝術</strong>：建築設計概念源於「洞窟」，白天像個大酒壺，晚上則會打上燈光，展現流動的建築之美。</li><li><strong>多元空間</strong>：內有大劇院、中劇院、小劇場三座專業劇場，以及空中花園、角落沙龍等藝術休閒空間。</li><li><strong>文創生活</strong>：館內設有各式文創商店、餐廳，讓藝術融入日常生活。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>場館參觀</strong>：免費</li><li><strong>演出票價</strong>：依各表演項目而定。</li><li><strong>導覽服務</strong>：需付費預約，詳情請洽官網。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>高鐵/火車</strong>：搭乘至高鐵台中站或台中火車站，轉乘 75、161、300 系列公車至「新光/遠百」站或「國家歌劇院」站。</li><li><strong>捷運</strong>：搭乘台中捷運綠線至「市政府」站，步行約 10-15 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.npac-ntt.org/",placeHolderTitle:"台中國家歌劇院 官方網站"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1822.427774029241!2d120.63833139999999!3d24.16293169999999!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x34693d91458e0a29%3A0x67399283f5024773!2z5ZyW5ZyW5Z-O5Z-O5Lq65LqM6aSo!5e0!3m2!1szh-TW!2stw!4v1703667469376!5m2!1szh-TW!2stw",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=t(h,[["render",m]]);export{b as __pageData,w as default};
