import{_ as t,I as i,c as l,o as n,a5 as o,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 台中 -> 景點 -> 高美濕地","description":"","frontmatter":{"title":"高美濕地"},"headers":[],"relativePath":"台灣/台中/景點/高美濕地.md","filePath":"docs/台灣/台中/景點/高美濕地.md","lastUpdated":1766811211000}'),h={name:"台灣/台中/景點/高美濕地.md"};function m(c,a,g,p,_,u){const r=i("PreviewLink");return n(),l("div",null,[a[0]||(a[0]=o('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/台中/景點/高美濕地.md for this page in Markdown format</div><h1 id="高美濕地" tabindex="-1">高美濕地 <a class="header-anchor" href="#高美濕地" aria-label="Permalink to “高美濕地”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/1/1c/Gaomei_Wetland%2C_Taichung%2C_Taiwan_%28555594035%29_3.jpg" alt="高美濕地" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>高美濕地位於台中市清水區大甲溪出海口南側，是一處面積廣達 700 多公頃的濕地生態保護區。這裡擁有台灣稀有的雲林莞草，並以絕美的水平面夕陽倒影，被譽為「世界七大天空之鏡」之一。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>天空之鏡</strong>：退潮後的薄水面能完美反射天空雲彩，夕陽時分展現如幻如夢的倒影景致。</li><li><strong>生態木棧道</strong>：長約 691 公尺的木棧道能讓遊客深入濕地核心而不破壞灘地，近距離觀察招潮蟹、彈塗魚及各式冬候鳥。</li><li><strong>高美風車</strong>：成排的巨型風力發電機是當地顯著的地標，在落日餘暉下構成壯觀的工業與自然美景。</li><li><strong>豐富生態</strong>：這裡是重要的鳥類棲息地，每年秋冬季吸引大量稀有候鳥在此棲息。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全區參觀</strong>：免費。</li><li><strong>木棧道開放</strong>：每日視潮汐時間開放，建議出發前查閱「高美濕地旅遊網」潮汐表。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>高鐵/火車</strong>：搭乘至台中火車站或高鐵台中站，轉乘公車（如 309 路）直達高美濕地。</li><li><strong>火車 (海線)</strong>：搭乘至「清水火車站」，轉乘 178、179 路公車。</li><li><strong>自行開車</strong>：國道 4 號清水端下，往省道台 17 線行駛即可抵達。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://taichung.travel/zh-tw/",placeHolderTitle:"高美濕地 - 臺中觀光旅遊網"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1819.570177721867!2d120.55110000000001!3d24.288200!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x346914660e5a639b%3A0x6b7713437a01c402!2z6auY57-N5rK55ZyY!5e0!3m2!1szh-TW!2stw!4v1703667688326!5m2!1szh-TW!2stw",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=t(h,[["render",m]]);export{b as __pageData,w as default};
