import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 台中 -> 活動 -> 台中爵士音樂節","description":"","frontmatter":{"title":"台中爵士音樂節"},"headers":[],"relativePath":"台灣/台中/活動/台中爵士音樂節.md","filePath":"docs/台灣/台中/活動/台中爵士音樂節.md","lastUpdated":1766811211000}'),h={name:"台灣/台中/活動/台中爵士音樂節.md"};function m(c,a,p,_,f,g){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/台中/活動/台中爵士音樂節.md for this page in Markdown format</div><h1 id="台中爵士音樂節" tabindex="-1">台中爵士音樂節 <a class="header-anchor" href="#台中爵士音樂節" aria-label="Permalink to “台中爵士音樂節”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/1/1d/Taichung_Jazz_Festival_Night.jpg" alt="台中爵士音樂節" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>台中爵士音樂節是亞洲最具指標性的爵士音樂盛會之一，自 2003 年起每年於台中市盛大舉行。每年十月，台中市民廣場與草悟道廊帶會化身為大型的爵士樂壇，吸引來自全球頂尖的爵士樂手同台競技，並有超過數十萬名樂迷坐在草地上，在涼爽的秋風中享受悠揚樂音。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>草地音樂會</strong>：這是台灣少見的大型戶外野餐式音樂會，樂迷們全家大小席地而坐，氛圍寫意自在。</li><li><strong>國際大師雲集</strong>：每年邀請獲葛萊美獎或在國際享譽盛名的爵士大師來台演出，規模與藝術品質位居全台之冠。</li><li><strong>跨界合作</strong>：結合了在地餐飲、文創市集與工作坊，讓市民在享受音樂之餘，也能體驗台中的創意生活圈。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>活動參與</strong>：免費（包含主舞台區域之所有表演）。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>高鐵轉乘</strong>：自高鐵台中站搭乘捷運至「市政府」站，轉乘公車或使用共享單車至草悟道與市民廣場。</li><li><strong>公車</strong>：搭乘台中市公車至「中正國小」站或「草悟道 (市民廣場)」站下車。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.taichungjazzfestival.tw/",placeHolderTitle:"台中爵士音樂節 官方網站"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1822.894371465243!2d120.6628999!3d24.1449999!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x34693d052a92c4cd%3A0xc3c94f5012301824!2z5ZyW5ZyW5biC5rK55ZyY6IyC!5e0!3m2!1szh-TW!2stw!4v1703667822998!5m2!1szh-TW!2stw",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=t(h,[["render",m]]);export{b as __pageData,w as default};
