import{_ as i,I as t,c as l,o,a5 as n,j as a,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 台北 -> 景點 -> 台北 101","description":"","frontmatter":{"title":"台北 101"},"headers":[],"relativePath":"台灣/台北/景點/台北101.md","filePath":"docs/台灣/台北/景點/台北101.md","lastUpdated":1766637247000}'),h={name:"台灣/台北/景點/台北101.md"};function m(c,e,p,g,u,_){const r=t("PreviewLink");return o(),l("div",null,[e[0]||(e[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/台北/景點/台北101.md for this page in Markdown format</div><h1 id="台北-101" tabindex="-1">台北 101 <a class="header-anchor" href="#台北-101" aria-label="Permalink to “台北 101”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Taipei_101_2009_amk.jpg" alt="台北 101" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>台北 101 是位於台灣台北市信義區的一座摩天大樓，為台灣第一高樓，在 2004 年至 2010 年間為世界第一高樓，至今仍是世界第十一高樓。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>建築設計</strong>：設計靈感來自於竹子，象徵著節節高升、生生不息的意涵。其獨特的「巨型結構」設計能抵禦強風與地震。</li><li><strong>觀景台</strong>：位於 89 樓的室內觀景台與 91 樓的戶外觀景台，提供 360 度俯瞰台北盆地的絕佳視野。</li><li><strong>世界級購物中心</strong>：大樓底部為頂級購物中心，匯集了全球各大精品品牌與米其林推薦餐廳。</li><li><strong>跨年煙火</strong>：每年舉行的跨年煙火表演是台北最具代表性的慶祝活動，吸引全球目光。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全票</strong>：新台幣 600 元</li><li><strong>優惠票</strong>：新台幣 540 元（學生、軍警）</li><li><strong>敬老票/愛心票</strong>：新台幣 300 元</li><li><em>註：票價僅供參考，最新資訊請以官方網站公告為準。</em></li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>捷運</strong>：搭乘台北捷運<strong>淡水信義線</strong>至「台北 101/世貿站」，從 4 號出口出站即可直達。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),a("ul",null,[a("li",null,[s(r,{url:"https://www.taipei-101.com.tw/",placeHolderTitle:"台北 101 官方網站"})])]),e[1]||(e[1]=a("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),a("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),e[2]||(e[2]=a("iframe",{src:"https://www.google.com/maps?output=embed&q=Taipei%20101%2C%20Taiwan&t=&z=15&ie=UTF8&iwloc=&output=embed",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=i(h,[["render",m]]);export{b as __pageData,w as default};
