import{_ as e,c as t,o as r,a5 as i}from"./chunks/framework.BtqC3pLB.js";const p=JSON.parse('{"title":"台灣 -> 台北 -> 景點 -> 陽明山國家公園","description":"","frontmatter":{},"headers":[],"relativePath":"台灣/台北/景點/陽明山.md","filePath":"docs/台灣/台北/景點/陽明山.md","lastUpdated":1766639418000}'),n={name:"台灣/台北/景點/陽明山.md"};function o(l,a,s,d,h,c){return r(),t("div",null,[...a[0]||(a[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/台北/景點/陽明山.md for this page in Markdown format</div><h1 id="陽明山國家公園" tabindex="-1">陽明山國家公園 <a class="header-anchor" href="#陽明山國家公園" aria-label="Permalink to “陽明山國家公園”">​</a></h1><p>陽明山國家公園是台灣北部唯一的火山國家公園，擁有豐富的火山地形、溫泉、瀑布和森林生態。每年春季的花季吸引大量遊客前來賞花。</p><h2 id="必訪景點" tabindex="-1">必訪景點 <a class="header-anchor" href="#必訪景點" aria-label="Permalink to “必訪景點”">​</a></h2><ul><li><strong>陽明公園：</strong> 花季時有海芋、櫻花等各式花卉盛開。</li><li><strong>擎天崗：</strong> 廣闊的草原景觀，可見牛群悠閒吃草。</li><li><strong>小油坑：</strong> 火山活動的後火山現象，可見硫磺噴氣孔。</li><li><strong>冷水坑：</strong> 台灣唯一的沉澱硫磺礦床，有牛奶湖景觀。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><p>可搭乘捷運至劍潭站或士林站，轉乘公車上山。假日期間部分路段實施交通管制。</p><h2 id="溫馨提示" tabindex="-1">溫馨提示 <a class="header-anchor" href="#溫馨提示" aria-label="Permalink to “溫馨提示”">​</a></h2><ul><li><strong>氣候多變：</strong> 山區氣候變化大，請注意保暖並攜帶雨具。</li><li><strong>交通管制：</strong> 花季期間及假日有交通管制，建議搭乘大眾運輸工具。</li></ul>',9)])])}const m=e(n,[["render",o]]);export{p as __pageData,m as default};
