import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 台東 -> 景點 -> 小野柳","description":"","frontmatter":{"title":"小野柳"},"headers":[],"relativePath":"台灣/台東/景點/小野柳.md","filePath":"docs/台灣/台東/景點/小野柳.md","lastUpdated":1766811211000}'),h={name:"台灣/台東/景點/小野柳.md"};function m(c,a,g,p,f,u){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/台東/景點/小野柳.md for this page in Markdown format</div><h1 id="小野柳-富岡地質公園" tabindex="-1">小野柳 (富岡地質公園) <a class="header-anchor" href="#小野柳-富岡地質公園" aria-label="Permalink to “小野柳 (富岡地質公園)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/d/df/%E5%B0%8F%E9%87%8E%E6%9F%B3.jpg" alt="小野柳" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>小野柳位於台東富岡漁港北方，現已更名為「富岡地質公園」。因其海岸地形與北海岸的野柳地質景觀驚人相似，故被稱為「小野柳」。這裡是一座大自然雕琢而成的岩石藝術館，展現了隆起珊瑚礁與厚層砂岩經過地殼變動、海蝕與風化作用後所呈現的奇特面貌。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>奇岩異石</strong>：園區內遍布單面山、豆腐岩、蕈狀岩及蜂窩岩，其中以「豆腐岩」與「單面山」最為壯觀。</li><li><strong>生長於岩石上的植物</strong>：可以看到白水木、林投等能在礁岩縫隙中艱困生長的濱海植物。</li><li><strong>露營區</strong>：設有設備完善的露營場地，遊客可以在星空與浪濤聲中體驗大自然生活。</li><li><strong>觀察潮間帶</strong>：在安全海域的岩縫中，常能發現寄居蟹、螃蟹及多樣的海洋生物。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>參觀門票</strong>：免費入場。</li><li><strong>停車費用</strong>：大型車 200 元、小型車 60 元、機車 20 元。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>公車</strong>：搭乘「台灣好行」東部海岸線或鼎東客運往成功、長濱方向之班車，於「小野柳」站下車。</li><li><strong>自行開車</strong>：位於台 11 線公路旁，離台東市區僅約 10-15 分鐘車程。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.eastcoast-nsa.gov.tw/zh-tw",placeHolderTitle:"富岡地質公園 (小野柳) - 東部海岸國家風景區管理處"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d734.9082260655495!2d121.19200871900138!3d22.793019307775924!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x346fb11f81d113c7%3A0x8673a67d169d13c7!2sFugang%20Geological%20Park!5e0!3m2!1szh-TW!2stw!4v1703666579893!5m2!1szh-TW!2stw",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=t(h,[["render",m]]);export{b as __pageData,w as default};
