import{_ as r,I as i,c as n,o,a5 as l,j as e,J as s}from"./chunks/framework.BtqC3pLB.js";const f=JSON.parse('{"title":"台灣 -> 基隆 -> 景點 -> 國立海洋科技博物館 (海科館)","description":"","frontmatter":{"title":"國立海洋科技博物館 (海科館)"},"headers":[],"relativePath":"台灣/基隆/景點/國立海洋科學博物館.md","filePath":"docs/台灣/基隆/景點/國立海洋科學博物館.md","lastUpdated":1766637247000}'),d={name:"台灣/基隆/景點/國立海洋科學博物館.md"};function c(_,a,h,m,g,p){const t=i("PreviewLink");return o(),n("div",null,[a[0]||(a[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/基隆/景點/國立海洋科學博物館.md for this page in Markdown format</div><h1 id="國立海洋科技博物館-海科館" tabindex="-1">國立海洋科技博物館 (海科館) <a class="header-anchor" href="#國立海洋科技博物館-海科館" aria-label="Permalink to “國立海洋科技博物館 (海科館)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/1/11/National_Museum_of_Marine_Science_and_Technology_Entrance.jpg" alt="海科館 外部建築" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>國立海洋科技博物館（簡稱海科館）位於基隆市八斗子漁港周邊，是全台灣唯一結合港口、在地文化與海洋科技教育的國家級博物館。其主建築體係由早期全台最大的「北部火力發電廠」廠址改建而成，巧妙融合了工業遺產與現代建築設計。館內規畫多樣化的展示廳與虛實整合的互動設施，是全台家庭客群假日旅遊與深度研學的首選重地。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>工業遺址改建</strong>：在參觀過程中能看見原發電廠遺留的巨型鋼構與建築特色，展現了極佳的空間活化創意。</li><li><strong>多樣化展示廳</strong>：包括兒童廳、海洋文化廳、海洋科學廳、水產廳等，從理論到實務全方位呈現人與大海的關係。</li><li><strong>IMAX 海洋劇場</strong>：全台最大、最先進的 IMAX 3D 劇院之一，透過震撼的影音帶領遊客探索深不可測的藍色星球。</li><li><strong>園區延伸景點</strong>：連結潮境公園、潮境智能海洋館與望幽谷步道，構築成一個龐大的「海洋微座城市」文化商圈。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>主題館全票</strong>：200 元（不含特殊特展及劇場）。</li><li><strong>優惠票</strong>：140 元（學生）。</li><li><strong>65 歲以上</strong>：100 元。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>火車專線</strong>：可搭乘台鐵深澳線至「海科館站」下車，是全台少數直接與博物館連結的特色小站。</li><li><strong>客運直達</strong>：自台北搭乘國光客運 1579、或是快捷公車 2088 路直達館門口。</li><li><strong>自行開車</strong>：沿台 62 線萬瑞快速道路南下，出口遵循指標前往八斗子。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(t,{url:"https://www.nmmst.gov.tw/",placeHolderTitle:"國立海洋科技博物館 官方網站"})])])])}const k=r(d,[["render",c]]);export{f as __pageData,k as default};
