import{_ as t,I as i,c as o,o as l,a5 as n,j as e,J as s}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"台灣 -> 基隆 -> 景點 -> 潮境公園","description":"","frontmatter":{"title":"潮境公園"},"headers":[],"relativePath":"台灣/基隆/景點/潮境公園.md","filePath":"docs/台灣/基隆/景點/潮境公園.md","lastUpdated":1766637247000}'),d={name:"台灣/基隆/景點/潮境公園.md"};function h(c,a,m,_,p,g){const r=i("PreviewLink");return l(),o("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/基隆/景點/潮境公園.md for this page in Markdown format</div><h1 id="潮境公園" tabindex="-1">潮境公園 <a class="header-anchor" href="#潮境公園" aria-label="Permalink to “潮境公園”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/2/25/Chaojing_Park_Brooms.jpg" alt="潮境公園 飛天掃帚" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>潮境公園位於基隆市八斗子半島東側，面積約 1 公頃，與八斗子漁港僅一線之隔。這裡原為長年堆放垃圾的掩埋場，經過重新設計規劃後，華麗轉身為一處臨海、綠草如茵的濱海公園。這裡不僅有著開闊的草坪可眺望九份、基隆山與著名的基隆嶼，更因其獨特的地景藝術裝置與絕美的海岸步道，成為東北角人氣極高的打卡聖地。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>哈利波特飛天掃帚</strong>：著名的地景藝術裝置，巨大的掃帚矗立在海邊斜坡，是遊客來此必拍的神奇造景。</li><li><strong>無敵海景步道</strong>：沿著海岸線延伸的步道，能欣賞潮間帶生態與湛藍海景，天氣晴朗時更可與基隆嶼同框。</li><li><strong>鸚鵡螺溜滑梯</strong>：富有創意的海洋造型遊樂設施，讓孩子們在玩耍中感受海洋生物的魅力。</li><li><strong>看海平台</strong>：多處設置完善的觀景台，是靜靜發呆、吹海風與捕捉九份夜景、海上漁火的最佳位置。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全區參觀</strong>：免費參觀。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>大眾運輸</strong>：可於基隆火車站搭乘 103 路公車至「海科館 (碧水巷) 站」或於瑞芳火車站搭乘 T99 台灣好行「濱海奇基線」。</li><li><strong>自行開車</strong>：經由中山高速公路（國道 1 號）大華系統交流道接台 62 線萬瑞快速道路，於八斗子交流道下即可抵達。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://tour.klcg.gov.tw/zh-tw/attractions/chaojing-park/",placeHolderTitle:"潮境公園 - 基隆旅遊網"})])])])}const f=t(d,[["render",h]]);export{k as __pageData,f as default};
