import{_ as t,I as r,c as n,o,a5 as l,j as e,J as s}from"./chunks/framework.BtqC3pLB.js";const f=JSON.parse('{"title":"台灣 -> 基隆 -> 景點 -> 潮境智能海洋館 (i OCEAN)","description":"","frontmatter":{"title":"潮境智能海洋館 (i OCEAN)"},"headers":[],"relativePath":"台灣/基隆/景點/潮境智能海洋館.md","filePath":"docs/台灣/基隆/景點/潮境智能海洋館.md","lastUpdated":1766637247000}'),d={name:"台灣/基隆/景點/潮境智能海洋館.md"};function c(h,a,_,m,p,g){const i=r("PreviewLink");return o(),n("div",null,[a[0]||(a[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/基隆/景點/潮境智能海洋館.md for this page in Markdown format</div><h1 id="潮境智能海洋館-i-ocean" tabindex="-1">潮境智能海洋館 (i OCEAN) <a class="header-anchor" href="#潮境智能海洋館-i-ocean" aria-label="Permalink to “潮境智能海洋館 (i OCEAN)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/e/e0/i_Ocean_Keelung_Entrance.jpg" alt="潮境智能海洋館 展區景象" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>潮境智能海洋館為國立海洋科技博物館旗下的先端場館，坐落於潮境公園內。該館巧妙規畫於原本的海科館「海洋中心」基礎之上，運用地理優勢，結合 5G 新興展示科技、VR 虛擬實境與 AR 擴增實境，打造出沉浸式的互動探索體驗。這裡是北北基首座結合海洋生物復育、科研考察與數位娛樂美學的「虛實整合」實境水族館，現已成為基隆最新的熱門知性景點。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>5G 沉浸式體驗</strong>：利用高速網路連結，遊客能與潛入深海的機器人即時同步，俯瞰潮境海灣下神祕的地形與繽紛生態。</li><li><strong>i Ocean 珊瑚溫室</strong>：展示超過 300 種、數千株色彩斑斕的熱帶魚與珍貴珊瑚礁生物，展現了海科館在海洋復育上的卓越成果。</li><li><strong>VR 海底隧道冒險</strong>：穿戴專業設備進入模擬艙，展開驚心動魄的深海大探險，挑戰與巨型海洋生物對視。</li><li><strong>山海連結景觀咖啡</strong>：館內設有能同時包覽海天一色與東北角山巒疊翠的半露天休閒空間，提供極佳的拍照休閒選擇。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全票</strong>：300 元。</li><li><strong>團體票/學生票</strong>：225 元。</li><li><strong>基隆市民</strong>：250 元。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>大眾運輸</strong>：可於基隆火車站搭乘 103、108 路公車至「海科館 (碧水巷) 站」下車，步行進入潮境公園。</li><li><strong>專線火車</strong>：搭乘台鐵平溪/深澳線至「海科館火車站」下車。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(i,{url:"https://iocean.nmmst.gov.tw/",placeHolderTitle:"潮境智能海洋館 官方網站"})])])])}const k=t(d,[["render",c]]);export{f as __pageData,k as default};
