import{_ as e,c as t,o as r,a5 as i}from"./chunks/framework.BtqC3pLB.js";const p=JSON.parse('{"title":"台灣 -> 宜蘭 -> 景點 -> 太平山國家森林遊樂區","description":"","frontmatter":{},"headers":[],"relativePath":"台灣/宜蘭/景點/太平山.md","filePath":"docs/台灣/宜蘭/景點/太平山.md","lastUpdated":1766642339000}'),n={name:"台灣/宜蘭/景點/太平山.md"};function o(l,a,s,d,h,c){return r(),t("div",null,[...a[0]||(a[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/宜蘭/景點/太平山.md for this page in Markdown format</div><h1 id="太平山國家森林遊樂區" tabindex="-1">太平山國家森林遊樂區 <a class="header-anchor" href="#太平山國家森林遊樂區" aria-label="Permalink to “太平山國家森林遊樂區”">​</a></h1><p>太平山國家森林遊樂區位於宜蘭縣大同鄉，以其原始森林、高山湖泊和多樣的動植物生態聞名。這裡的翠峰湖、見晴懷古步道和蹦蹦車是必訪的景點。</p><h2 id="必訪景點" tabindex="-1">必訪景點 <a class="header-anchor" href="#必訪景點" aria-label="Permalink to “必訪景點”">​</a></h2><ul><li><strong>翠峰湖：</strong> 台灣最大的高山湖泊，湖光山色美不勝收。</li><li><strong>見晴懷古步道：</strong> 由舊鐵道改建，可欣賞雲海、山嵐變幻。</li><li><strong>蹦蹦車：</strong> 搭乘懷舊的森林鐵路小火車，體驗森林浴。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><p>建議自行開車前往，或搭乘國光客運【1750】羅東 - 太平山線。山路蜿蜒，請小心駕駛。</p><h2 id="溫馨提示" tabindex="-1">溫馨提示 <a class="header-anchor" href="#溫馨提示" aria-label="Permalink to “溫馨提示”">​</a></h2><ul><li><strong>氣候：</strong> 高山氣候涼爽，請準備保暖衣物。</li><li><strong>生態：</strong> 保持園區環境整潔，不餵食野生動物。</li></ul>',9)])])}const m=e(n,[["render",o]]);export{p as __pageData,m as default};
