import{_ as e,c as t,o as r,a5 as i}from"./chunks/framework.BtqC3pLB.js";const p=JSON.parse('{"title":"台灣 -> 宜蘭 -> 景點 -> 礁溪溫泉","description":"","frontmatter":{},"headers":[],"relativePath":"台灣/宜蘭/景點/礁溪溫泉.md","filePath":"docs/台灣/宜蘭/景點/礁溪溫泉.md","lastUpdated":1766639418000}'),n={name:"台灣/宜蘭/景點/礁溪溫泉.md"};function o(l,a,s,d,h,_){return r(),t("div",null,[...a[0]||(a[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/宜蘭/景點/礁溪溫泉.md for this page in Markdown format</div><h1 id="礁溪溫泉" tabindex="-1">礁溪溫泉 <a class="header-anchor" href="#礁溪溫泉" aria-label="Permalink to “礁溪溫泉”">​</a></h1><p>礁溪溫泉位於宜蘭縣礁溪鄉，是台灣著名的溫泉區，以其無色無味、富含鈉、鎂、鈣、鉀離子及碳酸氫根的碳酸氫鈉泉質而聞名。泡湯後皮膚光滑，因此有「美人湯」之稱。</p><h2 id="泡湯體驗" tabindex="-1">泡湯體驗 <a class="header-anchor" href="#泡湯體驗" aria-label="Permalink to “泡湯體驗”">​</a></h2><ul><li><strong>溫泉飯店：</strong> 礁溪溫泉區飯店林立，提供多樣的溫泉住宿選擇。</li><li><strong>溫泉公園：</strong> 設有免費泡腳池，讓遊客輕鬆享受溫泉樂趣。</li><li><strong>湯圍溝溫泉公園：</strong> 結合日式庭園造景，設有免費泡腳池和付費湯屋。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><p>搭乘火車至礁溪車站，或從台北搭乘國道客運，皆可直達礁溪溫泉區。</p><h2 id="溫馨提示" tabindex="-1">溫馨提示 <a class="header-anchor" href="#溫馨提示" aria-label="Permalink to “溫馨提示”">​</a></h2><ul><li><strong>溫泉禮儀：</strong> 泡湯前請先淋浴，勿在溫泉池中搓洗身體。</li><li><strong>周邊美食：</strong> 礁溪有許多特色小吃，如溫泉拉麵、宜蘭蔥油餅等。</li></ul>',9)])])}const m=e(n,[["render",o]]);export{p as __pageData,m as default};
