import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 宜蘭 -> 景點 -> 羅東夜市","description":"","frontmatter":{"title":"羅東夜市"},"headers":[],"relativePath":"台灣/宜蘭/景點/羅東夜市.md","filePath":"docs/台灣/宜蘭/景點/羅東夜市.md","lastUpdated":1766830308000}'),h={name:"台灣/宜蘭/景點/羅東夜市.md"};function m(c,a,p,g,u,_){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/宜蘭/景點/羅東夜市.md for this page in Markdown format</div><h1 id="羅東夜市" tabindex="-1">羅東夜市 <a class="header-anchor" href="#羅東夜市" aria-label="Permalink to “羅東夜市”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/6/69/%E7%BE%85%E6%9D%B1%E5%A4%9C%E5%B8%82_-_panoramio.jpg" alt="羅東夜市" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>羅東夜市位於宜蘭縣羅東鎮，環繞著中央的「羅東中山公園」，是宜蘭地區最具代表性與人氣的夜間景點。這裡以豐富多樣且具有在地特色的傳統風味小吃聞名，不僅是宜蘭人解決宵夜的首選，更是遊客造訪宜蘭不可錯過的食尚聖地。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>排隊美食雲集</strong>：包括著名的阿灶伯當歸羊肉湯、魏姐包心粉圓、卜肉與糕渣、以及蔥多到滿出來的三星蔥油餅。</li><li><strong>公園綠地結合</strong>：夜市緊鄰中山公園，遊客買完小吃後常選擇在公園的長椅上席地而坐，一邊乘涼一邊品嚐美食。</li><li><strong>流行服飾與雜貨</strong>：除了美食區，周邊街道也林立著各類運動品牌旗艦店與平價服飾店，滿足購物需求。</li><li><strong>友善的步行環境</strong>：夜市區域內採人車分道或時段性行人徒步區，讓遊客能安心逛街。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全區參觀</strong>：免費參觀。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>火車</strong>：搭乘至「羅東火車站」，由站前路步行約 10 分鐘即可抵達。</li><li><strong>客運</strong>：自台北搭乘葛瑪蘭客運或首都客運至羅東轉運站，步行約 15 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.e-land.gov.tw/",placeHolderTitle:"宜蘭縣政府 - 觀光資訊"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=Luodong+Night+Market",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const k=t(h,[["render",m]]);export{b as __pageData,k as default};
