import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 屏東 -> 景點 -> 國立海洋生物博物館","description":"","frontmatter":{"title":"國立海洋生物博物館"},"headers":[],"relativePath":"台灣/屏東/景點/國立海洋生物博物館.md","filePath":"docs/台灣/屏東/景點/國立海洋生物博物館.md","lastUpdated":1766641411000}'),h={name:"台灣/屏東/景點/國立海洋生物博物館.md"};function m(c,a,g,p,u,_){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/屏東/景點/國立海洋生物博物館.md for this page in Markdown format</div><h1 id="國立海洋生物博物館" tabindex="-1">國立海洋生物博物館 <a class="header-anchor" href="#國立海洋生物博物館" aria-label="Permalink to “國立海洋生物博物館”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/5/51/NMMBA_II.jpg" alt="國立海洋生物博物館" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>國立海洋生物博物館（簡稱海生館）位於屏東縣車城鄉，緊鄰後灣，是台灣最大的海洋生物博物館。全館以「海洋教育」與「生態保育」為核心，透過壯觀的展覽與先進的互動科技，引領遊客探索蔚藍海洋的奧秘。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>台灣水域館</strong>：展示台灣從山區溪流、水庫到近海的生態，其中「大洋池」餵食秀是必看亮點。</li><li><strong>珊瑚王國館</strong>：擁有長達 84 公尺的海底隧道，讓遊客如漫步在五彩斑斕的珊瑚礁迷宮。</li><li><strong>世界水域館</strong>：結合 3D 模擬與實體展示，探索極地水域、深海水域與巨藻森林。</li><li><strong>夜宿海生館</strong>：提供獨特的夜宿體驗，讓遊客能在魚群的陪伴下入眠，深度認識海洋生態。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全票</strong>：新台幣 450 元。</li><li><strong>優待票</strong>：新台幣 250 元（6 歲以上學生、65 歲以上長者）。</li><li><strong>免費對象</strong>：未滿 6 歲或身高未滿 115 公分之兒童、領有身心障礙手冊者及其陪伴者一名。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>高鐵轉乘</strong>：自高鐵左營站搭乘「墾丁快線」公車至南保力站，轉乘屏東客運「墾丁街車」(橘線) 直達海生館。</li><li><strong>火車轉乘</strong>：搭乘至「枋寮火車站」，轉乘往墾丁方向之公車站點至南保力，再轉乘墾丁街車。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.nmmba.gov.tw/",placeHolderTitle:"國立海洋生物博物館 官方網站"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=National+Museum+of+Marine+Biology+and+Aquarium",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=t(h,[["render",m]]);export{b as __pageData,w as default};
