import{_ as i,I as t,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 屏東 -> 景點 -> 小琉球","description":"","frontmatter":{"title":"小琉球"},"headers":[],"relativePath":"台灣/屏東/景點/小琉球.md","filePath":"docs/台灣/屏東/景點/小琉球.md","lastUpdated":1766641411000}'),h={name:"台灣/屏東/景點/小琉球.md"};function c(m,a,p,u,g,_){const r=t("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/屏東/景點/小琉球.md for this page in Markdown format</div><h1 id="小琉球" tabindex="-1">小琉球 <a class="header-anchor" href="#小琉球" aria-label="Permalink to “小琉球”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/5/5a/Vase_Rock%2C_Xiaoliuqiu.jpg" alt="花瓶岩" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>小琉球（琉球嶼）位於屏東縣東港鎮西南方，是台灣唯一的珊瑚礁島嶼。島上氣候溫暖，海水清澈見底，是全台灣觀賞綠蠵龜最高機率的地點。無污染的海洋生態、各具特色的海蝕洞與純樸的島民文化，使其成為南台灣最受歡迎的離島旅遊聖地。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>花瓶岩</strong>：島上的地標標誌，因地表受地殼隆起作用與海蝕影響，形似插著花草的巨型花瓶。</li><li><strong>浮潛與水上活動</strong>：在專業教練帶領下，有極高機會與巨大的綠蠵龜在水中並肩同遊。</li><li><strong>三大風景區</strong>：包含美人洞、山豬溝與烏鬼洞，可悠遊於迷宮般的石灰岩地形與熱帶林蔭間。</li><li><strong>潮間帶探索</strong>：退潮後可觀察海星、海膽及各式色彩繽紛的海洋生物，生物多樣性極高。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>島嶼進入</strong>：免費。</li><li><strong>三大風景區套票</strong>：全票約 120 元（包含美人洞、山豬溝、烏鬼洞）。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>渡輪</strong>：自屏東「東港碼頭」搭乘交通船（民營或公營），約 25-30 分鐘即可抵達小琉球。</li><li><strong>島內交通</strong>：租借電動機車或機車是環島最便利的方式，環島一周僅需約 30 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.pthg.gov.tw/liuchiu/",placeHolderTitle:"琉球鄉公所 官方網站"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=Liuqiu+Island",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const k=i(h,[["render",c]]);export{b as __pageData,k as default};
