import{_ as o,I as i,c as t,o as l,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const w=JSON.parse('{"title":"台灣 -> 新竹 -> 景點 -> 六福村主題遊樂園","description":"","frontmatter":{"title":"六福村主題遊樂園"},"headers":[],"relativePath":"台灣/新竹/景點/六福村主題遊樂園.md","filePath":"docs/台灣/新竹/景點/六福村主題遊樂園.md","lastUpdated":1766830308000}'),h={name:"台灣/新竹/景點/六福村主題遊樂園.md"};function m(c,a,p,g,_,u){const r=i("PreviewLink");return l(),t("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/新竹/景點/六福村主題遊樂園.md for this page in Markdown format</div><h1 id="六福村主題遊樂園" tabindex="-1">六福村主題遊樂園 <a class="header-anchor" href="#六福村主題遊樂園" aria-label="Permalink to “六福村主題遊樂園”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/a/ae/Leofoo_Village_Theme_Park_Entry.jpg" alt="六福村主題遊樂園" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>六福村主題遊樂園位於新竹縣關西鎮，是亞洲第一座結合大自然森林、五星級飯店與野生動物園的主題樂園。園區占地廣大，分為四大主題區：美國大西部、南太平洋、阿拉伯皇宮及非洲部落。這裡不僅擁有各類刺激的機械遊樂設施，更能近距離觀察大型非洲野生動物，是全台灣極受歡迎的家庭旅遊聖地。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>四大主題村</strong>：每個區域都擁有截然不同的異國風情與專屬的刺激設施，如「笑傲飛鷹」與「大怒神」。</li><li><strong>非洲部落 (野生動物園)</strong>：全台唯一的開放式野生動物園，遊客可搭乘蒸氣火車或猛獸巴士，近距離觀賞白犀牛、斑馬及孟加拉虎。</li><li><strong>六福水樂園</strong>：夏季限定開放，擁有各式水上滑道與仿真海浪池。</li><li><strong>豪華表演與遊行</strong>：每日定時舉辦的大型花車巡遊與劇場表演，展現魔幻與歡樂的派對氣息。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>成人票</strong>：1199 元（通常現場與線上購票有優惠價，約 750-999 元）。</li><li><strong>學童票/博愛票</strong>：699 元 / 599 元。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>巴士</strong>：自台北（松山機場、公館、景安站）搭乘台聯客運 5350 路直達六福村。</li><li><strong>火車轉乘</strong>：自中壢火車站搭乘新竹客運「中壢至六福村」班車。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.leofoo.com.tw/",placeHolderTitle:"六福村主題遊樂園 (六福旅遊集團)"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=No.+60,+Gongzihgou,+Ren-an+Village,+Guansi+Township,+Hsinchu+County+306,+Taiwan",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const b=o(h,[["render",m]]);export{w as __pageData,b as default};
