import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const f=JSON.parse('{"title":"台灣 -> 桃園 -> 景點 -> Xpark 水族館","description":"","frontmatter":{"title":"Xpark 水族館"},"headers":[],"relativePath":"台灣/桃園/景點/Xpark水族館.md","filePath":"docs/台灣/桃園/景點/Xpark水族館.md","lastUpdated":1766637247000}'),p={name:"台灣/桃園/景點/Xpark水族館.md"};function h(c,a,m,k,g,u){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/桃園/景點/Xpark水族館.md for this page in Markdown format</div><h1 id="xpark-水族館" tabindex="-1">Xpark 水族館 <a class="header-anchor" href="#xpark-水族館" aria-label="Permalink to “Xpark 水族館”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/3/3d/Xpark_Aquarium_Taoyuan_Entrance.jpg" alt="Xpark 水族館" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>Xpark 位於桃園市中壢區，由日本橫濱八景島海島樂園海外首度直營。這是一座結合了先進科技與生物多樣性的「都會型水生公園」。全館樓高三層，將氣溫、濕度、味道及聲音結合空間藝術與生物展示，帶領遊客展開一段超現實的海洋探索旅程。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>福爾摩沙展區</strong>：高達 8 公尺的巨型水族缸展示了台灣豐富的水域生態，成千上萬的銀鱗鯧群壯觀巡游。</li><li><strong>癒見水母區</strong>：利用萬花筒般的玻璃鏡面與變換無常的虹彩燈光，搭配久石讓大師打造的音樂，呈現如夢似幻的水母世界。</li><li><strong>企鵝奇遇區</strong>：展示麥哲倫企鵝與國王企鵝，設有獨特的企鵝空中走道，可近距離觀察企鵝在遊客頭頂滑游的靈動姿態。</li><li><strong>珊瑚潛行區</strong>：利用地面的互動式影音投影與實體珊瑚礁結合，讓人彷彿置身於絢麗的熱帶海底景觀。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>成人票</strong>：550 元。</li><li><strong>學生票</strong>：400 元 (12 歲以上學生)。</li><li><strong>孩童票/博愛票</strong>：250 元 (4 歲以上學童或 65 歲以上長者)。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>高鐵/捷運</strong>：搭乘高鐵至「桃園站」或桃園機場捷運至「A18 高鐵桃園站」，由出口連通橋步行約 5 分鐘即可抵達。</li><li><strong>自行開車</strong>：鄰近國道 1 號及國道 2 號，可將車輛停放在置地廣場桃園停車場。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://www.xpark.com.tw/",placeHolderTitle:"Xpark 水族館 官方網站"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=No.+105%2C+Chunde+Rd%2C+Zhongli+District%2C+Taoyuan+City%2C+Taiwan+320",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const b=t(p,[["render",h]]);export{f as __pageData,b as default};
