import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 高雄 -> 景點 -> 蓮池潭","description":"","frontmatter":{"title":"蓮池潭"},"headers":[],"relativePath":"台灣/高雄/景點/蓮池潭.md","filePath":"docs/台灣/高雄/景點/蓮池潭.md","lastUpdated":1766811211000}'),h={name:"台灣/高雄/景點/蓮池潭.md"};function m(c,a,p,g,f,_){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/高雄/景點/蓮池潭.md for this page in Markdown format</div><h1 id="蓮池潭" tabindex="-1">蓮池潭 <a class="header-anchor" href="#蓮池潭" aria-label="Permalink to “蓮池潭”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/9/90/Dragon_and_Tiger_Pagodas_02.jpg" alt="龍虎塔" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>蓮池潭位於高雄市左營區，是一個面積廣大的人工湖泊。湖畔環繞著密集的寺廟與寶塔，其中最著名的莫過於「龍虎塔」，是高雄最具代表性的國際級觀光景點。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>龍虎塔</strong>：蓮池潭的地標。遊客需遵從「從龍口進、由虎口出」的傳統，寓意為「消災增長福慧」。</li><li><strong>春秋閣</strong>：為紀念武聖關公而建，由兩座中國宮殿式樓閣組成，中間有九曲橋相連。</li><li><strong>玄天上帝神像</strong>：擁有世界最高的水上神像之一，氣勢磅礡。</li><li><strong>環潭步道</strong>：湖畔設有完善的環潭自行車道與散步道，並提供水上活動如滑水體驗。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>全區開放</strong>：免費參觀。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>捷運/高鐵</strong>：搭乘捷運紅線或高鐵至「左營站」，步行約 15-20 分鐘或轉乘公車、使用共享單車前往。</li><li><strong>台鐵</strong>：搭乘至「左營（舊城）站」，出站後即可抵達湖畔。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://khh.travel/zh-tw/attractions/detail/25",placeHolderTitle:"蓮池潭風景區 - 高雄旅遊網"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1840.407765163482!2d120.29630000000001!3d22.6875!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x346e0f279d026369%3A0x6b6009623e846069!2z6JGJ5Y2B5rK5!5e0!3m2!1szh-TW!2stw!4v1703668380277!5m2!1szh-TW!2stw",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const k=t(h,[["render",m]]);export{b as __pageData,k as default};
