import{_ as t,I as i,c as l,o,a5 as n,j as e,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"台灣 -> 高雄 -> 景點 -> 西子灣","description":"","frontmatter":{"title":"西子灣"},"headers":[],"relativePath":"台灣/高雄/景點/西子灣.md","filePath":"docs/台灣/高雄/景點/西子灣.md","lastUpdated":1766641411000}'),h={name:"台灣/高雄/景點/西子灣.md"};function c(m,a,p,g,u,_){const r=i("PreviewLink");return o(),l("div",null,[a[0]||(a[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /台灣/高雄/景點/西子灣.md for this page in Markdown format</div><h1 id="西子灣" tabindex="-1">西子灣 <a class="header-anchor" href="#西子灣" aria-label="Permalink to “西子灣”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/d/d4/Kaohsiung_Sizihwan_Sunset.jpg" alt="西子灣夕照" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to “簡介”">​</a></h2><p>西子灣位於高雄市西側，北靠壽山、南隔高雄港與旗津相望。這裡是一處以夕陽美景與天然礁石聞名的灣澳。每當日落時分，餘暉映照在波光粼粼的大海上，交織出一幅絕美的光影畫卷。此地鄰接打狗英國領事館與中山大學，匯集了自然歷史與文藝氣息。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>西子灣夕照</strong>：名列高雄八景之一，是情侶散步與攝影愛好者捕捉日落海景的絕佳地點。</li><li><strong>打狗英國領事館</strong>：位於山頂的後文藝復興時代建築，可居高臨下俯瞰整個高雄港進出全景。</li><li><strong>國立中山大學</strong>：校園依山傍海，其著名的「海堤」是民眾吹海風、看夕陽的熱門去處。</li><li><strong>雄鎮北門</strong>：與對岸旗後砲台相互遙望，是維繫高雄港門戶安全的清代軍事古蹟。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>西子灣景區</strong>：免費進入。</li><li><strong>打狗英國領事館</strong>：全票 99 元。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>捷運</strong>：搭乘高雄捷運橘線至「哈瑪星站」(O1)，轉乘公車或租借 YouBike 前往。</li><li><strong>輕軌</strong>：搭乘高雄輕軌至「哈瑪星站」(C14) 下車，步行或騎行巡迴西子灣周邊景點。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),e("ul",null,[e("li",null,[s(r,{url:"https://khh.travel/zh-tw/attractions/detail/21",placeHolderTitle:"西子灣 - 高雄旅遊網"})])]),a[1]||(a[1]=e("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[2]||(a[2]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=Sizihwan+Bay",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const k=t(h,[["render",c]]);export{b as __pageData,k as default};
