import{_ as a,c as r,o as t,a3 as n}from"./chunks/framework.Coj2bmTX.js";const f=JSON.parse('{"title":"尼泊爾 -> 交通 -> 機場交通","description":"","frontmatter":{},"headers":[],"relativePath":"尼泊爾/交通/機場交通.md","filePath":"docs/尼泊爾/_交通/機場交通.md","lastUpdated":1737303891000}'),o={name:"尼泊爾/交通/機場交通.md"};function i(m,e,s,d,l,p){return t(),r("div",null,[...e[0]||(e[0]=[n('<h1 id="機場交通" tabindex="-1">機場交通 <a class="header-anchor" href="#機場交通" aria-label="Permalink to “機場交通”">​</a></h1><p>從特里布萬國際機場 (尼泊爾語：त्रिभुवन विमानस्थल；英語：Tribhuvan International Airport；IATA 代碼：KTM；ICAO 代碼：VNKT) 前往加德滿都市區塔美爾 (Thamel)，最常見的方法是搭乘計程車或是預約飯店安排的接機車輛。</p><p>在機場搭乘計程車時，若有人幫忙搬運行李，容易被收取小費，若是不打算付小費，必須盡量避免行李被他人碰觸。</p><p>若是從市區前往機場，可請飯店協助預約計程車。</p><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2><p>特里布萬國際機場 (Tribhuvan International Airport)</p><iframe src="https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d14130.450862352742!2d85.3592326!3d27.6983624!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x39eb1a266b342bc5%3A0x73bbfa829a89af1b!2sTribhuvan%20International%20Airport!5e0!3m2!1sen!2stw!4v1690715630712!5m2!1sen!2stw" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><p>加德滿都市區塔美爾 (Thamel)</p><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d7064.146918107784!2d85.30711051364372!3d27.71501810228048!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x39eb18fcb77fd4bd%3A0x58099b1deffed8d4!2sThamel%2C%20Kathmandu%2044600%2C%20Nepal!5e0!3m2!1sen!2stw!4v1690715665096!5m2!1sen!2stw" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2><ul><li><a href="https://www.backpackers.com.tw/forum/showthread.php?t=1456" target="_blank" rel="noreferrer">塔美（Thamel）吃喝玩樂大滿足情報 機場－塔美</a></li></ul>',11)])])}const c=a(o,[["render",i]]);export{f as __pageData,c as default};
