import{_ as e,c as l,o as r,a3 as t}from"./chunks/framework.Coj2bmTX.js";const p=JSON.parse('{"title":"尼泊爾 -> 注意事項 -> 簽證與入境須知","description":"","frontmatter":{},"headers":[],"relativePath":"尼泊爾/注意事項/簽證與入境須知.md","filePath":"docs/尼泊爾/_注意事項/簽證與入境須知.md","lastUpdated":1718124115000}'),i={name:"尼泊爾/注意事項/簽證與入境須知.md"};function o(n,a,_,s,d,c){return r(),l("div",null,[...a[0]||(a[0]=[t('<h1 id="簽證與入境須知" tabindex="-1">簽證與入境須知 <a class="header-anchor" href="#簽證與入境須知" aria-label="Permalink to “簽證與入境須知”">​</a></h1><h2 id="簽證" tabindex="-1">簽證 <a class="header-anchor" href="#簽證" aria-label="Permalink to “簽證”">​</a></h2><ul><li><p>尼泊爾內政部於 2022 年 6 月 19 日公告：取消經由陸路或航空入境尼國旅客須提出完全接種過兩劑 COVID-19 疫苗之證明抑或 PCR 檢測陰性報告。</p></li><li><p>持中華民國護照民眾可申辦尼泊爾落地簽證，惟事由僅限於觀光。停留期限分為 15 天、30 天及 90 天。</p></li><li><p>建議國人於出國前可至領事事務局網站進行「出國登錄」，輸入個人旅行資料，以利駐外館處在發生緊急事件時及時取得聯繫。另倘國人在尼國境內遭遇急難效協助，請即撥打中華民國駐印度代表處緊急聯絡電話：+91-9810-502-610；或請國內親友撥打「外交部緊急聯絡中心」電話：0800-085-095，以便提供必要協助。</p></li></ul><h2 id="入境須知" tabindex="-1">入境須知 <a class="header-anchor" href="#入境須知" aria-label="Permalink to “入境須知”">​</a></h2><ul><li><p>尼泊爾入境及出境之安檢非常嚴格徹底，國人赴該國旅遊請留意，勿攜帶違禁品。</p></li><li><p>尼泊爾為外滙管制嚴格之國家，攜帶外幣入境逾 5000 美元則須申報。倘未申報，則所攜之外幣將遭收沒，並處以所攜外幣同額罰款，並限制出境。</p></li></ul><h2 id="資料來源" tabindex="-1">資料來源 <a class="header-anchor" href="#資料來源" aria-label="Permalink to “資料來源”">​</a></h2><ul><li><a href="https://www.boca.gov.tw/sp-foof-countrycp-01-124-24014-02-1.html" target="_blank" rel="noreferrer">尼泊爾簽證及入境須知</a></li></ul>',7)])])}const f=e(i,[["render",o]]);export{p as __pageData,f as default};
