import{_ as e,c as r,o as i,a3 as l}from"./chunks/framework.Coj2bmTX.js";const p=JSON.parse('{"title":"日本 -> 北海道 -> 北海道","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/index.md","filePath":"docs/日本/北海道/index.md","lastUpdated":1742649666000}'),t={name:"日本/北海道/index.md"};function n(o,a,s,d,h,c){return i(),r("div",null,[...a[0]||(a[0]=[l('<h1 id="北海道" tabindex="-1">北海道 <a class="header-anchor" href="#北海道" aria-label="Permalink to “北海道”">​</a></h1><h2 id="北國風光" tabindex="-1">北國風光 <a class="header-anchor" href="#北國風光" aria-label="Permalink to “北國風光”">​</a></h2><p>北海道是日本第二大島，也是最北端的行政區，以其壯麗的自然風光、豐富的戶外活動和美味的海鮮而聞名。</p><h2 id="必訪景點" tabindex="-1">必訪景點 <a class="header-anchor" href="#必訪景點" aria-label="Permalink to “必訪景點”">​</a></h2><ul><li><strong>函館山：</strong> 以其美麗的夜景而聞名，可以俯瞰函館市的景色。</li><li><strong>旭山動物園：</strong> 以其獨特的動物展示方式而聞名，可以近距離觀察各種動物。</li><li><strong>大沼國定公園：</strong> 位於渡島半島東側，是北海道內面積最小的國定公園，可以欣賞美麗的湖光山色。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><p>前往北海道可以搭乘飛機至新千歲機場或函館機場，或是搭乘新幹線至札幌車站。在北海道內移動，JR 北海道鐵路周遊券是一個非常划算的選擇。</p><h2 id="溫馨提示" tabindex="-1">溫馨提示 <a class="header-anchor" href="#溫馨提示" aria-label="Permalink to “溫馨提示”">​</a></h2><ul><li>北海道的冬季非常寒冷，請注意保暖。</li><li>北海道以其美味的海鮮而聞名，例如螃蟹和海膽，不妨品嚐一下當地的特產。</li><li>北海道的面積廣闊，建議事先規劃好行程，以便更好地體驗當地的自然風光。</li></ul>',9)])])}const m=e(t,[["render",n]]);export{p as __pageData,m as default};
