import{_ as n,I as o,c as s,o as i,a5 as h,j as e,J as t}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 北海道 -> 交通 -> 北海道巴士交通指南","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/交通/巴士.md","filePath":"docs/日本/北海道/_交通/巴士.md","lastUpdated":1766918002000}'),l={name:"日本/北海道/交通/巴士.md"};function g(c,a,p,d,f,u){const r=o("PreviewLink");return i(),s("div",null,[a[0]||(a[0]=h('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/交通/巴士.md for this page in Markdown format</div><h1 id="北海道巴士交通指南" tabindex="-1">北海道巴士交通指南 <a class="header-anchor" href="#北海道巴士交通指南" aria-label="Permalink to “北海道巴士交通指南”">​</a></h1><p>在廣大的北海道，巴士是連接城市與景點的重要工具，特別是鐵路無法到達的區域。</p><h2 id="城市間高速巴士-intercity-highway-bus" tabindex="-1">城市間高速巴士 (Intercity Highway Bus) <a class="header-anchor" href="#城市間高速巴士-intercity-highway-bus" aria-label="Permalink to “城市間高速巴士 (Intercity Highway Bus)”">​</a></h2><p>連接 <strong><a href="./../札幌/">札幌</a></strong> 與 <strong><a href="./../旭川/">旭川</a></strong>、<strong><a href="./../函館/">函館</a></strong>、帶廣、釧路等主要城市。</p><h3 id="主要路線與營運公司" tabindex="-1">主要路線與營運公司 <a class="header-anchor" href="#主要路線與營運公司" aria-label="Permalink to “主要路線與營運公司”">​</a></h3><ul><li><strong>起訖點：<a href="./../札幌/">札幌</a> ↔ <a href="./../函館/">函館</a> (高速函館號)</strong>：<a href="https://www.chuo-bus.co.jp/highway/index.cgi?ope=det&amp;n=9" target="_blank" rel="noreferrer">時刻表與預約</a></li><li><strong>起訖點：<a href="./../札幌/">札幌</a> ↔ <a href="./../旭川/">旭川</a> (高速旭川號)</strong>：<a href="https://www.chuo-bus.co.jp/highway/index.cgi?ope=det&amp;n=8" target="_blank" rel="noreferrer">時刻表</a></li><li><strong>起訖點：<a href="./../札幌/">札幌</a> ↔ <a href="./../富良野/">富良野</a> (高速富良野號)</strong>：<a href="https://www.chuo-bus.co.jp/highway/index.cgi?ope=det&amp;n=13" target="_blank" rel="noreferrer">時刻表</a></li><li><strong>起訖點：<a href="./../札幌/">札幌</a> ↔ <a href="./../帶廣/">帶廣</a> (Potato Liner)</strong>：<a href="https://www.chuo-bus.co.jp/highway/index.cgi?ope=det&amp;n=10" target="_blank" rel="noreferrer">時刻表與預約</a></li><li><strong>起訖點：<a href="./../札幌/">札幌</a> ↔ <a href="./../釧路/">釧路</a> (Starlight Kushiro)</strong>：<a href="https://www.chuo-bus.co.jp/highway/index.cgi?ope=det&amp;n=11" target="_blank" rel="noreferrer">時刻表與預約</a></li><li><strong>起訖點：<a href="./../札幌/">札幌</a> ↔ <a href="./../登別/">登別溫泉</a> (高速登別溫泉號)</strong>：<a href="https://www.donanbus.co.jp/highway/index.php?id=1" target="_blank" rel="noreferrer">時刻表</a></li></ul><hr><h2 id="觀光巴士-sightseeing-bus" tabindex="-1">觀光巴士 (Sightseeing Bus) <a class="header-anchor" href="#觀光巴士-sightseeing-bus" aria-label="Permalink to “觀光巴士 (Sightseeing Bus)”">​</a></h2><p>適合想要一天遊覽多個景點但不打算自駕的旅客。</p><ul><li><strong>定期觀光巴士</strong>：由<a href="https://teikan.chuo-bus.co.jp/tw/" target="_blank" rel="noreferrer">北海道中央巴士</a>營運，提供 <strong><a href="./../札幌/">札幌</a></strong> 出發的美瑛富良野一日遊、支笏湖洞爺湖一日遊等。<a href="https://teikan.chuo-bus.co.jp/tw/" target="_blank" rel="noreferrer">路線與時刻表</a></li><li><strong>東北海道巴士 (Eastern Hokkaido Bus)</strong>：主要在冬季運行，連接 <strong><a href="./../網走/">網走</a></strong>、<strong><a href="./../知床/">知床</a></strong>、摩周湖等區域。<a href="https://easthokkaido.com/bus/" target="_blank" rel="noreferrer">路線與時刻表</a></li></ul><hr><h2 id="市區公車與在地巴士-local-bus" tabindex="-1">市區公車與在地巴士 (Local Bus) <a class="header-anchor" href="#市區公車與在地巴士-local-bus" aria-label="Permalink to “市區公車與在地巴士 (Local Bus)”">​</a></h2><p>除了長途巴士，以下是前往熱門景點常用的在地巴士路線：</p><ul><li><strong><a href="./../札幌/">札幌</a> ↔ 定山溪溫泉 (河童 Liner)</strong>：<a href="https://www.jotetsu.co.jp/bus/kappa_liner/index.html" target="_blank" rel="noreferrer">時刻表與預約</a></li><li><strong><a href="./../旭川/">旭川</a> ↔ 層雲峽 (道北巴士)</strong>：前往層雲峽冰瀑祭的主要方式。<a href="https://www.dohokubus.com/rosen_bus3.html" target="_blank" rel="noreferrer">時刻表</a></li><li><strong><a href="./../千歲/">千歲</a> ↔ <a href="./../千歲/">支笏湖</a> (中央巴士)</strong>：前往冰濤祭的交通手段。<a href="https://www.chuo-bus.co.jp/city_route/course/chitose/index.php" target="_blank" rel="noreferrer">時刻表</a></li><li><strong><a href="./../札幌/">札幌</a> ↔ <a href="./../洞爺湖/">洞爺湖</a> (道南巴士)</strong>：<a href="https://www.donanbus.co.jp/highway/index.php?id=5" target="_blank" rel="noreferrer">時刻表與預約</a></li><li><strong><a href="./../帶廣/">帶廣</a> ↔ 然別湖 (拓殖巴士)</strong>：前往冰上村的唯一公共交通。<a href="https://www.takubus.com/" target="_blank" rel="noreferrer">時刻表</a></li></ul><hr><h2 id="乘車須知" tabindex="-1">乘車須知 <a class="header-anchor" href="#乘車須知" aria-label="Permalink to “乘車須知”">​</a></h2><ol><li><strong>上車抽取整理券</strong>：若不使用 IC 卡，從後門上車時需抽取整理券，下車時依前方螢幕顯示金額投幣。</li><li><strong>預約制</strong>：大部分長途高速巴士為「預約制」，建議事先在線上或便利商店購買。</li><li><strong>IC 卡使用</strong>：目前北海道主要城市已可使用全國共通 IC 卡 (如 Suica)，但在偏遠地區仍可能只收現金。</li></ol><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',19)),e("ul",null,[e("li",null,[t(r,{url:"https://www.chuo-bus.co.jp/",placeHolderTitle:"北海道中央巴士官方網站"})]),e("li",null,[t(r,{url:"https://japanbusonline.com/zh-tw",placeHolderTitle:"Japan Bus Online (巴士線上預約)"})])])])}const _=n(l,[["render",g]]);export{b as __pageData,_ as default};
