import{_ as t,I as o,c as n,o as s,a5 as d,j as e,J as l,a as i}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 北海道 -> 函館 -> 景點 -> 金森紅磚倉庫","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/函館/景點/金森紅磚倉庫.md","filePath":"docs/日本/北海道/函館/景點/金森紅磚倉庫.md","lastUpdated":1766811211000}'),m={name:"日本/北海道/函館/景點/金森紅磚倉庫.md"};function h(g,a,p,u,c,f){const r=o("PreviewLink");return s(),n("div",null,[a[0]||(a[0]=d('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/函館/景點/金森紅磚倉庫.md for this page in Markdown format</div><h1 id="金森紅磚倉庫" tabindex="-1">金森紅磚倉庫 <a class="header-anchor" href="#金森紅磚倉庫" aria-label="Permalink to “金森紅磚倉庫”">​</a></h1><p>金森紅磚倉庫位於函館灣區域，是函館最具代表性的觀光景點之一。這些由紅磚砌成的倉庫群建於明治時期，見證了函館作為國際貿易港口的繁榮歷史。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/3/3b/Kanemori_Red_Brick_Warehouse_20140807.jpg" alt="金森紅磚倉庫" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>歷史建築再利用：</strong> 昔日的營業倉庫現在改建為綜合購物中心、餐廳與活動空間，保留了古樸的外觀與內部的木造結構。</li><li><strong>四大館區：</strong><ul><li><strong>金森洋物館：</strong> 販售各式精緻雜貨、服飾與伴手禮。</li><li><strong>函館歷史廣場：</strong> 包含啤酒館與手工藝品店。</li><li><strong>金森廳：</strong> 多功能活動場地。</li><li><strong>BAY 函館：</strong> 設有運河景觀餐廳與精品店。</li></ul></li><li><strong>浪漫夜景：</strong> 夜晚時分，紅磚牆會被打上溫暖的燈光，與海港景色相映成趣，非常適合散步。</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒040-0053 北海道函館市末廣町 14-12</li><li><strong>電話：</strong> +81 138-27-5530</li><li><strong>營業時間：</strong> 09:30 - 19:00 (各店鋪可能有所不同)</li><li><strong>門票：</strong> 免費進入。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>路面電車：</strong> 搭乘函館市電至「十字街」站下車，步行約 5 分鐘。</li><li><strong>步行：</strong> 從 JR 函館站步行約 15-20 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),e("ul",null,[e("li",null,[l(r,{url:"https://hakodate-kanemori.com",placeHolderTitle:"金森紅磚倉庫官方網站"})]),e("li",null,[l(r,{url:"https://www.hakodate.travel/cht/sightseeing-spots/view/kanemori-red-brick-warehouse/",placeHolderTitle:"函館市官方觀光指南 - 金森紅磚倉庫"})])]),a[1]||(a[1]=e("h2",{id:"溫馨提示",tabindex:"-1"},[i("溫馨提示 "),e("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),a[2]||(a[2]=e("ul",null,[e("li",null,"這裡有許多函館限定的甜點與伴手禮（如起司蛋糕、烏賊相關產品），值得一逛。"),e("li",null,"冬季期間，倉庫前的海面上會裝飾巨大的聖誕樹，氣氛非常熱鬧。")],-1)),a[3]||(a[3]=e("h2",{id:"地圖",tabindex:"-1"},[i("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[4]||(a[4]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3001.12!2d140.7175!3d41.7661!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2z6YeR5qOu57SF56O95YCJ5bqr!5e0!3m2!1szh-TW!2sjp!4v1734627200000",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const k=t(m,[["render",h]]);export{b as __pageData,k as default};
