import{_ as e,c as r,o as t,a5 as i}from"./chunks/framework.BtqC3pLB.js";const p=JSON.parse('{"title":"日本 -> 北海道 -> 旭川 -> 旭川","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/旭川/index.md","filePath":"docs/日本/北海道/旭川/index.md","lastUpdated":1766918002000}'),n={name:"日本/北海道/旭川/index.md"};function l(d,a,o,s,h,c){return t(),r("div",null,[...a[0]||(a[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/旭川.md for this page in Markdown format</div><h1 id="旭川" tabindex="-1">旭川 <a class="header-anchor" href="#旭川" aria-label="Permalink to “旭川”">​</a></h1><h2 id="北海道的心臟" tabindex="-1">北海道的心臟 <a class="header-anchor" href="#北海道的心臟" aria-label="Permalink to “北海道的心臟”">​</a></h2><p>旭川是北海道第二大城市，位於北海道的中心位置。這裡以其優質的旭川拉麵、精緻的工藝品以及著名的旭山動物園而聞名。</p><h2 id="必訪景點" tabindex="-1">必訪景點 <a class="header-anchor" href="#必訪景點" aria-label="Permalink to “必訪景點”">​</a></h2><ul><li><strong><a href="./景點/旭山動物園">旭山動物園</a>：</strong> 日本最受歡迎的動物園之一，以其「行動展示」方式讓遊客近距離觀察動物的自然行為。</li><li><strong><a href="./景點/上野農場">上野農場</a>：</strong> 美麗的英式花園，展現獨特的「北海道風」景觀。</li><li><strong><a href="./景點/男山酒造資料館">男山酒造資料館</a>：</strong> 了解日本清酒的釀造歷史，並品嚐優質的清酒。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><p>從札幌搭乘特急列車前往旭川約需 1.5 小時。旭川也是前往富良野和美瑛的重要門戶。</p><h2 id="溫馨提示" tabindex="-1">溫馨提示 <a class="header-anchor" href="#溫馨提示" aria-label="Permalink to “溫馨提示”">​</a></h2><ul><li>旭山動物園在冬季會有企鵝散步活動，非常受歡迎。</li><li>旭川拉麵以醬油湯底著稱，是來到這裡必嚐的美食。</li></ul>',10)])])}const f=e(n,[["render",l]]);export{p as __pageData,f as default};
