import{_ as o,I as n,c as t,o as i,a5 as l,j as e,J as s}from"./chunks/framework.BtqC3pLB.js";const f=JSON.parse('{"title":"日本 -> 北海道 -> 旭川 -> 景點 -> 上野農場 (Ueno Farm)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/旭川/景點/上野農場.md","filePath":"docs/日本/北海道/旭川/景點/上野農場.md","lastUpdated":1766946043000}'),d={name:"日本/北海道/旭川/景點/上野農場.md"};function m(c,a,h,_,p,u){const r=n("PreviewLink");return i(),t("div",null,[a[0]||(a[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/旭川/景點/上野農場.md for this page in Markdown format</div><h1 id="上野農場-ueno-farm" tabindex="-1">上野農場 (Ueno Farm) <a class="header-anchor" href="#上野農場-ueno-farm" aria-label="Permalink to “上野農場 (Ueno Farm)”">​</a></h1><p>上野農場是由著名景觀設計師上野砂由紀 (Sayuyuki Ueno) 所設計的「北海道花園」。這裡將傳統的英式花園風格與北海道的氣候與土壤相結合，創造出獨特的「北海道風花園」。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/c/ce/Ueno_Farm_%2819310228258%29.jpg" alt="上野農場" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>四季花毯：</strong> 隨著季節更迭，農場內的花卉不斷變換，從春季的鬱金香到夏季的多年生草本植物，景觀豐富多彩。</li><li><strong>地精山 (Gnome&#39;s Hill)：</strong> 登上農場後方的小山丘，可以俯瞰整座花園與周邊的鄉間田野景色。</li><li><strong>咖啡館與雜貨：</strong> 由舊倉庫改建的 NAYA 咖啡館提供當地食材製作的甜點與輕食。</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒071-8122 北海道旭川市永山町 16 丁目 186</li><li><strong>門票：</strong> 成人 1,000 日圓。</li><li><strong>開放時間：</strong> 每年 4 月下旬至 10 月中旬 (10:00 - 17:00)。冬季不對外開放。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>火車：</strong> 搭乘 JR 石北本線至「櫻岡站」，下車後步行約 15 分鐘。詳見 <strong><a href="./../../交通/鐵路與地鐵">JR 列車指南</a></strong>。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),e("ul",null,[e("li",null,[s(r,{url:"https://uenofarm.jp/",placeHolderTitle:"上野農場官方網站"})])])])}const k=o(d,[["render",m]]);export{f as __pageData,k as default};
