import{_ as a,c as r,o as t,a3 as i}from"./chunks/framework.Coj2bmTX.js";const f=JSON.parse('{"title":"日本 -> 北海道 -> 景點 -> 北海道神宮","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/景點/北海道神宮.md","filePath":"docs/日本/北海道/景點/北海道神宮.md","lastUpdated":1737303891000}'),l={name:"日本/北海道/景點/北海道神宮.md"};function o(s,e,d,n,_,c){return t(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="北海道神宮" tabindex="-1">北海道神宮 <a class="header-anchor" href="#北海道神宮" aria-label="Permalink to “北海道神宮”">​</a></h1><p>北海道神宮是位於北海道札幌市中央區的一座神社。北海道總鎮守。在北海道開拓初期，北海道地區多與樺太、千島等地相對望，而且為了針對俄羅斯的防守需要，因此神宮的正門是向著東北方的。神宮中的分神社「開拓神社」祭祀了一群對於北海道開拓事業有功勞的人物（如島義勇、間宮林藏）。</p><ul><li>地址 〒064-8505 北海道札幌市中央区宮ケ丘 474</li><li>電話號碼 +81116110261</li><li>交通手段 鄰近札幌市營地下鐵東西線円山公園駅。</li><li>營業時間 6:00 ～ 17:00</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2915.3867433741793!2d141.30560411547842!3d43.05433297914642!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x5f0b29dcc4f0a893%3A0xec8de2355b66d708!2sHokkaido%20Jingu!5e0!3m2!1sen!2stw!4v1690545398740!5m2!1sen!2stw" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2><ul><li><a href="https://www.sapporo.travel/zh-tw/spot/facility/hokkaido_shrine/" target="_blank" rel="noreferrer">一般社團法人札幌觀光協會 - 北海道神宮</a></li></ul>',7)])])}const h=a(l,[["render",o]]);export{f as __pageData,h as default};
