import{_ as o,I as i,c as n,o as s,a5 as d,j as e,J as l,a as t}from"./chunks/framework.BtqC3pLB.js";const _=JSON.parse('{"title":"日本 -> 北海道 -> 札幌 -> 景點 -> 大通公園","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/札幌/景點/大通公園.md","filePath":"docs/日本/北海道/札幌/景點/大通公園.md","lastUpdated":1766946043000}'),h={name:"日本/北海道/札幌/景點/大通公園.md"};function p(m,a,f,u,c,g){const r=i("PreviewLink");return s(),n("div",null,[a[0]||(a[0]=d('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/札幌/景點/大通公園.md for this page in Markdown format</div><h1 id="大通公園" tabindex="-1">大通公園 <a class="header-anchor" href="#大通公園" aria-label="Permalink to “大通公園”">​</a></h1><p>大通公園位於札幌市中心，橫跨東西約 1.5 公里，是札幌的象徵，也是市民休憩的場所。</p><p><a href="https://odori-park.jp/miryoku/photo-gallery/" target="_blank" rel="noreferrer">大通公園官方相片藝廊</a><em>圖片來源：公益財團法人札幌市公園綠化協會</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><p>大通公園將札幌市中心分為南北兩部分。公園內種滿了花草樹木，四季景色分明。這裡也是舉辦各種大型活動的場地，最著名的莫過於冬季的「札幌雪祭」。</p><h2 id="主要活動" tabindex="-1">主要活動 <a class="header-anchor" href="#主要活動" aria-label="Permalink to “主要活動”">​</a></h2><ul><li><strong><a href="./../../活動/札幌雪祭">札幌雪祭</a> (2 月)：</strong> 展示巨大的冰雕與雪雕，是大通公園年度最盛大的活動。</li><li><strong>丁香節 (5 月)：</strong> 慶祝初夏到來的祭典。</li><li><strong><a href="./../../活動/#夏季-7月-8月">啤酒花園</a> (7-8 月)：</strong> 日本規模最大的戶外啤酒花園。</li><li><strong>白色燈樹節 (11-12 月)：</strong> 璀璨的燈飾裝點冬夜。</li></ul><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>公園進入：</strong> 免費。</li><li><strong>札幌電視塔 (展望台)：</strong> 成人 1,000 日圓。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>地鐵：</strong> 搭乘南北線、東西線或東豐線至「大通站」下車即達。詳見 <strong><a href="./../../交通/鐵路與地鐵">札幌地鐵指南</a></strong>。</li><li><strong>步行：</strong> 從 JR 札幌站步行約 10-15 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',13)),e("ul",null,[e("li",null,[l(r,{url:"https://odori-park.jp/",placeHolderTitle:"大通公園官方網站"})]),e("li",null,[l(r,{url:"https://www.snowfes.com/",placeHolderTitle:"札幌雪祭官網"})]),e("li",null,[l(r,{url:"https://www.tv-tower.co.jp/",placeHolderTitle:"札幌電視塔官網"})])]),a[1]||(a[1]=e("h2",{id:"溫馨提示",tabindex:"-1"},[t("溫馨提示 "),e("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),a[2]||(a[2]=e("ul",null,[e("li",null,"公園內的「烤玉米」攤位是札幌的特色美食，推薦嘗試。"),e("li",null,"札幌電視塔位於公園的最東端（1 丁目），是俯瞰公園全景的最佳位置。")],-1)),a[3]||(a[3]=e("h2",{id:"地圖",tabindex:"-1"},[t("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[4]||(a[4]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1165.1!2d141.3464!3d43.0597!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2z5aSn6YCa5YWs5ZyS!5e0!3m2!1szh-TW!2sjp!4v1734627200000",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const w=o(h,[["render",p]]);export{_ as __pageData,w as default};
