import{_ as t,I as n,c as o,o as i,a5 as l,j as a,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"日本 -> 北海道 -> 洞爺湖 -> 景點 -> 有珠山纜車 (Usuzan Ropeway)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/洞爺湖/景點/有珠山纜車.md","filePath":"docs/日本/北海道/洞爺湖/景點/有珠山纜車.md","lastUpdated":1766946043000}'),h={name:"日本/北海道/洞爺湖/景點/有珠山纜車.md"};function u(p,e,m,c,_,g){const r=n("PreviewLink");return i(),o("div",null,[e[0]||(e[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/洞爺湖/景點/有珠山纜車.md for this page in Markdown format</div><h1 id="有珠山纜車-usuzan-ropeway" tabindex="-1">有珠山纜車 (Usuzan Ropeway) <a class="header-anchor" href="#有珠山纜車-usuzan-ropeway" aria-label="Permalink to “有珠山纜車 (Usuzan Ropeway)”">​</a></h1><p>有珠山是一座至今仍活動頻繁的活火山，最近一次噴發是在 2000 年。搭乘有珠山纜車，只需 6 分鐘即可抵達山頂，近距離觀察火山地貌並俯瞰洞爺湖全景。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/b/b2/The_View_from_Usuzan_Ropeway.jpg" alt="有珠山" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>火山口展望台：</strong> 從山頂站步行短距離即可抵達，可以近距離看到巨大的銀沼大火山口，至今依然冒著白煙。</li><li><strong>洞爺湖展望台：</strong> 從另一個角度俯瞰圓形的洞爺湖、湖中的中島，以及遠處形似富士山的「羊蹄山」。</li><li><strong>昭和新山：</strong> 纜車站就位於昭和新山腳下，這是一座在 1943-1945 年間突然隆起的「活生生」火山。</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒052-0102 北海道有珠郡壯瞥町昭和新山 184-5</li><li><strong>門票 (纜車來回)：</strong> 成人 1,800 日圓，兒童 900 日圓。</li><li><strong>開放時間：</strong> 08:00 - 18:00 (視季節調整)。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>巴士：</strong> 從 JR 洞爺站搭乘道南巴士（往昭和新山方向）至終點站。詳見 <strong><a href="./../../交通/巴士">北海道巴士指南</a></strong>。</li><li><strong>自駕：</strong> 從洞爺湖溫泉街開車約 10 - 15 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),a("ul",null,[a("li",null,[s(r,{url:"https://usuzan.hokkaido.jp/tw/",placeHolderTitle:"有珠山纜車官方網站"})])]),e[1]||(e[1]=a("h2",{id:"溫馨提示",tabindex:"-1"},[d("溫馨提示 "),a("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),e[2]||(e[2]=a("ul",null,[a("li",null,"山頂風力通常較大，建議準備擋風外套。"),a("li",null,"纜車站旁的「昭和新山熊牧場」也是熱門的親子景點。")],-1))])}const b=t(h,[["render",u]]);export{k as __pageData,b as default};
