import{_ as t,I as n,c as o,o as i,a5 as l,j as a,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 北海道 -> 登別 -> 景點 -> 大湯沼 (Oyunuma)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/登別/景點/大湯沼.md","filePath":"docs/日本/北海道/登別/景點/大湯沼.md","lastUpdated":1766946043000}'),u={name:"日本/北海道/登別/景點/大湯沼.md"};function m(h,e,p,_,c,g){const r=n("PreviewLink");return i(),o("div",null,[e[0]||(e[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/登別/景點/大湯沼.md for this page in Markdown format</div><h1 id="大湯沼-oyunuma" tabindex="-1">大湯沼 (Oyunuma) <a class="header-anchor" href="#大湯沼-oyunuma" aria-label="Permalink to “大湯沼 (Oyunuma)”">​</a></h1><p>大湯沼是日和山火山口噴發後形成的葫蘆形火山口湖，周長約 1 公里。湖底噴出約 130 度的硫磺泉，使湖面始終籠罩在熱氣之中。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/5/5f/Oyunuma_Pond%2C_Noboribetsu_Onsen%2C_Hokkaido%2C_April_2023_08.jpg" alt="大湯沼" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>震撼的火山口湖：</strong> 巨大的湖泊冒著滾滾熱氣，湖水呈灰黑色，背景是依然冒著白煙的「日和山」。</li><li><strong>奧大湯沼：</strong> 位於大湯沼旁的一個較小的沼澤，泉水呈現美麗的藍灰色。</li><li><strong>展望台：</strong> 沿著步道可以爬上高處，俯瞰大湯沼的全景，特別是秋季紅葉時期，色彩對比極其強烈。</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒059-0551 北海道登別市登別溫泉町</li><li><strong>門票：</strong> 免費進入。</li><li><strong>停車費：</strong> 500 日圓 (通常與地獄谷停車場共用票券)。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>步行：</strong> 從地獄谷沿著步道步行約 20 - 30 分鐘可抵達。</li><li><strong>自駕：</strong> 從溫泉街開車約 5 - 10 分鐘。詳見 <strong><a href="./../../交通/租車">北海道租車指南</a></strong>。</li><li><strong>巴士：</strong> 往返札幌或千歲的交通，請參考 <strong><a href="./../../交通/巴士">北海道巴士指南</a></strong>。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),a("ul",null,[a("li",null,[s(r,{url:"https://noboribetsu-spa.jp/spot/spot0055/",placeHolderTitle:"登別國際觀光會議協會 - 大湯沼"})])]),e[1]||(e[1]=a("h2",{id:"溫馨提示",tabindex:"-1"},[d("溫馨提示 "),a("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),e[2]||(e[2]=a("ul",null,[a("li",null,"步道坡度較陡，建議穿著適合運動的鞋子。"),a("li",null,"此處硫磺濃度更高，請注意安全。")],-1))])}const k=t(u,[["render",m]]);export{b as __pageData,k as default};
