import{_ as t,I as i,c as l,o as n,a5 as s,j as e,J as d,a as r}from"./chunks/framework.BtqC3pLB.js";const f=JSON.parse('{"title":"日本 -> 北海道 -> 知床 -> 景點 -> 知床五湖 (Shiretoko Five Lakes)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/知床/景點/知床五湖.md","filePath":"docs/日本/北海道/知床/景點/知床五湖.md","lastUpdated":1766946043000}'),h={name:"日本/北海道/知床/景點/知床五湖.md"};function m(c,a,g,p,k,u){const o=i("PreviewLink");return n(),l("div",null,[a[0]||(a[0]=s('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/知床/景點/知床五湖.md for this page in Markdown format</div><h1 id="知床五湖-shiretoko-five-lakes" tabindex="-1">知床五湖 (Shiretoko Five Lakes) <a class="header-anchor" href="#知床五湖-shiretoko-five-lakes" aria-label="Permalink to “知床五湖 (Shiretoko Five Lakes)”">​</a></h1><p>知床五湖是位於知床半島原始森林中的五個神祕湖泊，被譽為「知床的寶石」。這裡不僅可以欣賞到湖泊倒映知床連峰的美景，也是近距離接觸世界自然遺產生態的最佳地點。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/f/f1/Shiretoko_Five_Lakes_20140811-1.jpg" alt="知床五湖" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>高架木棧道：</strong> 全長約 800 公尺，遊客可以安全地走到一湖湖畔。棧道設有防熊電網，且坡度平緩，適合所有年齡層。</li><li><strong>地面遊步道：</strong> 穿梭在原始森林中，可以依序遊覽五個湖泊。由於此處是棕熊的棲息地，在棕熊活動旺季（5-7 月）需參加由導遊帶領的自費行程。</li><li><strong>倒映美景：</strong> 天氣晴朗時，知床連峰（如硫磺山、羅臼岳）會完美地倒映在靜謐的湖面上。</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒099-4356 北海道斜里郡斜里町遠音別村</li><li><strong>門票：</strong> 高架木棧道免費。地面遊步道在特定期間需付費參加講習或導覽。</li><li><strong>開放時間：</strong> 4 月下旬至 11 月上旬 (冬季封閉)。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>巴士：</strong> 從「知床斜里站」或「Utoro 溫泉總站」搭乘斜里巴士（知床線）至「知床五湖」站下車。詳見 <strong><a href="./../../交通/巴士">北海道巴士指南</a></strong>。</li><li><strong>自駕：</strong> 從 Utoro 溫泉區出發約 20 分鐘。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),e("ul",null,[e("li",null,[d(o,{url:"https://www.goko.go.jp/",placeHolderTitle:"知床五湖官方網站"})])]),a[1]||(a[1]=e("h2",{id:"溫馨提示",tabindex:"-1"},[r("溫馨提示 "),e("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),a[2]||(a[2]=e("ul",null,[e("li",null,[e("strong",null,"遇到棕熊"),r("：此處常有棕熊出沒，進入地面遊步道前請務必仔細聽取安全講習。")]),e("li",null,[e("strong",null,"服裝建議"),r("：即使是夏季，山區氣候也較為涼爽且多變，建議穿著便於步行的鞋子並攜帶防風外套。")])],-1))])}const b=t(h,[["render",m]]);export{f as __pageData,b as default};
