import{_ as t,I as o,c as n,o as s,a5 as d,j as e,J as r,a as i}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"日本 -> 北海道 -> 美瑛 -> 景點 -> 白髭瀑布","description":"","frontmatter":{},"headers":[],"relativePath":"日本/北海道/美瑛/景點/白髭瀑布.md","filePath":"docs/日本/北海道/美瑛/景點/白髭瀑布.md","lastUpdated":1766918002000}'),m={name:"日本/北海道/美瑛/景點/白髭瀑布.md"};function h(p,a,g,u,c,f){const l=o("PreviewLink");return s(),n("div",null,[a[0]||(a[0]=d('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/北海道/美瑛/景點/白髭瀑布.md for this page in Markdown format</div><h1 id="白髭瀑布" tabindex="-1">白髭瀑布 <a class="header-anchor" href="#白髭瀑布" aria-label="Permalink to “白髭瀑布”">​</a></h1><p>白髭瀑布位於白金溫泉區，是美瑛川源頭的一部分。瀑布從岩石縫隙中湧出，呈現出無數條細長的白色水流，宛如老人的白鬍鬚，因而得名。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/e/e1/Shirahige_Falls%2C_Biei_River%2C_Hokkaido%2C_Japan.jpg" alt="白髭瀑布" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>潛流瀑布：</strong> 不同於一般的河川瀑布，白髭瀑布是地下水從斷崖裂縫中噴湧而出的「潛流瀑布」，這在日本相當罕見。</li><li><strong>鈷藍色溪流：</strong> 瀑布落入的美瑛川因含有鋁等礦物質，呈現出迷人的鈷藍色，因此又被稱為「藍色河流」。</li><li><strong>四季美景：</strong><ul><li><strong>夏季：</strong> 清涼的瀑布與周邊翠綠的森林交相輝映。</li><li><strong>冬季：</strong> 瀑布周邊的岩石結成冰柱，與藍色溪流及白雪形成強烈對比，夜晚有點燈活動，景色極其壯麗。</li></ul></li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒071-0235 北海道上川郡美瑛町白金</li><li><strong>門票：</strong> 免費參觀。</li><li><strong>開放時間：</strong> 全天開放（建議從「美瑛川橋」上俯瞰）。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>巴士：</strong> 從 JR 美瑛站搭乘道北巴士（往白金溫泉方向）至「白金溫泉」站下車，步行約 5 分鐘。</li><li><strong>自駕：</strong> 從美瑛市區出發約需 25 分鐘，距離青池僅約 5 分鐘車程。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),e("ul",null,[e("li",null,[r(l,{url:"https://www.visit-hokkaido.jp/spot/detail_10188.html",placeHolderTitle:"HOKKAIDO LOVE! - 白髭瀑布"})]),e("li",null,[r(l,{url:"https://tokachidake-geopark.jp/geoname/246/",placeHolderTitle:"十勝岳地理公園 - 白髭瀑布"})])]),a[1]||(a[1]=e("h2",{id:"溫馨提示",tabindex:"-1"},[i("溫馨提示 "),e("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),a[2]||(a[2]=e("ul",null,[e("li",null,"觀賞瀑布的最佳位置是橫跨美瑛川的「藍河大橋」（Blue River Bridge）。"),e("li",null,"冬季期間，瀑布周邊會結冰，拍照時請注意安全並做好防寒措施。")],-1)),a[3]||(a[3]=e("h2",{id:"地圖",tabindex:"-1"},[i("地圖 "),e("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),a[4]||(a[4]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2881.12!2d142.6392!3d43.4747!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2z55m96ay65rCR5biD!5e0!3m2!1szh-TW!2sjp!4v1734627200000",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const b=t(m,[["render",h]]);export{k as __pageData,b as default};
