import{_ as d,c as a,o as e,a3 as r}from"./chunks/framework.Coj2bmTX.js";const c=JSON.parse('{"title":"日本 -> 富山 -> 活動 -> 立山黑部路線","description":"","frontmatter":{},"headers":[],"relativePath":"日本/富山/活動/黑部立山路線.md","filePath":"docs/日本/富山/活動/黑部立山路線.md","lastUpdated":1718124799000}'),l={name:"日本/富山/活動/黑部立山路線.md"};function h(n,t,o,m,i,s){return e(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="立山黑部路線" tabindex="-1">立山黑部路線 <a class="header-anchor" href="#立山黑部路線" aria-label="Permalink to “立山黑部路線”">​</a></h1><p>搭乘各種交通工具，穿越中部山岳國家公園的日本北阿爾卑斯山脈，中途經過黑部水壩、黑部平、大觀峰、雪之大谷、美女平等景點，此路線亦稱立山黑部阿爾卑斯路線。</p><h2 id="通行時間" tabindex="-1">通行時間 <a class="header-anchor" href="#通行時間" aria-label="Permalink to “通行時間”">​</a></h2><p>立山黑部地區屬於大雪區，每年開山時間多為四月至十一月期間。詳細情況可參考<a href="https://www.alpen-route.com/tw/" target="_blank" rel="noreferrer">官方網站</a>。</p><h2 id="交通工具" tabindex="-1">交通工具 <a class="header-anchor" href="#交通工具" aria-label="Permalink to “交通工具”">​</a></h2><p>完整走完立山黑部路線將透過多種交通工具，包含電鐵、登山纜車、無軌電車、空中纜車、巴士等，詳細的票價與時程可以參考<a href="https://www.alpen-route.com/tw/timetable/" target="_blank" rel="noreferrer">官方網站</a>。</p><table tabindex="0"><thead><tr><th>交通工具</th><th>起點站 (海拔)</th><th>終點站 (海拔)</th><th>搭車所需時間</th></tr></thead><tbody><tr><td>富山地方鐵道</td><td>電鐵富山</td><td>立山 (475m)</td><td>65 分</td></tr><tr><td>立山登山纜車</td><td>立山 (475m)</td><td>美女平 (977m)</td><td>7 分</td></tr><tr><td>立山高原巴士</td><td>美女平 (977m)</td><td>室堂 (2450m)</td><td>50 分</td></tr><tr><td>立山隧道無軌電車</td><td>室堂 (2450m)</td><td>大觀峰 (2316m)</td><td>10 分</td></tr><tr><td>立山空中纜車</td><td>大觀峰 (2316m)</td><td>黑部平 (1828m)</td><td>7 分</td></tr><tr><td>黑部登山纜車</td><td>黑部平 (1828m)</td><td>黑部湖 (1455m)</td><td>5 分</td></tr><tr><td>關電隧道電器巴士</td><td>黑部水壩 (1470m)</td><td>扇澤 (1433m)</td><td>16 分</td></tr><tr><td>市區巴士</td><td>扇澤 (1433m)</td><td>信濃大町 (893m)</td><td>40 分</td></tr></tbody></table><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2><ul><li><a href="https://www.alpen-route.com/tw/" target="_blank" rel="noreferrer">立山黑部阿爾卑斯路線</a></li></ul>',9)])])}const p=d(l,[["render",h]]);export{c as __pageData,p as default};
