import{_ as i,I as o,c as t,o as l,a5 as n,j as a,a as s,J as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 山梨 -> 景點 -> 河口湖紅葉迴廊 (Momiji Corridor)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/山梨/景點/河口湖紅葉迴廊.md","filePath":"docs/日本/山梨/景點/河口湖紅葉迴廊.md","lastUpdated":1766900024000}'),m={name:"日本/山梨/景點/河口湖紅葉迴廊.md"};function h(c,e,p,f,_,g){const r=o("PreviewLink");return l(),t("div",null,[e[0]||(e[0]=n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/山梨/景點/河口湖紅葉迴廊.md for this page in Markdown format</div><h1 id="河口湖紅葉迴廊-momiji-corridor" tabindex="-1">河口湖紅葉迴廊 (Momiji Corridor) <a class="header-anchor" href="#河口湖紅葉迴廊-momiji-corridor" aria-label="Permalink to “河口湖紅葉迴廊 (Momiji Corridor)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/0/02/Maple_Corridor_%2816191466966%29.jpg" alt="河口湖紅葉迴廊秋景" loading="lazy"></p><p>河口湖紅葉迴廊是富士五湖地區最具代表性的賞楓勝地。每到秋季，河口湖北岸梨川兩岸的 60 多棵巨型楓樹會轉為火紅，形成一條長達 150 公尺的夢幻隧道。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>楓葉隧道</strong>：沿著河川漫步，被密集的紅葉遮天蔽日，景色極其壯觀。</li><li><strong>夜間點燈</strong>：在「富士河口湖紅葉祭」期間，傍晚會進行點燈，營造出與白天截然不同的神祕浪漫氛圍。</li><li><strong>富士山與紅葉</strong>：在迴廊周邊可以拍到紅葉與富士山共舞的經典畫面。</li></ul><h2 id="賞楓時間" tabindex="-1">賞楓時間 <a class="header-anchor" href="#賞楓時間" aria-label="Permalink to “賞楓時間”">​</a></h2><ul><li><strong>最佳觀賞期</strong>：通常在 11 月上旬至 11 月下旬。</li><li><strong>紅葉祭</strong>：每年 11 月會舉辦紅葉祭，現場有許多美食攤位與手工藝品市集。</li></ul><h2 id="基本資訊" tabindex="-1">基本資訊 <a class="header-anchor" href="#基本資訊" aria-label="Permalink to “基本資訊”">​</a></h2><ul><li><strong>地址</strong>：〒401-0304 山梨県南都留郡富士河口湖町河口</li><li><strong>門票</strong>：免費參觀。</li></ul><h2 id="交通方式" tabindex="-1">交通方式 <a class="header-anchor" href="#交通方式" aria-label="Permalink to “交通方式”">​</a></h2><ul><li>搭乘<strong>河口湖周遊巴士 (紅線)</strong> 至「久保田一竹美術館」站下車即達。</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',13)),e[1]||(e[1]=a("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3247.1284888127434!2d138.75975767560604!3d35.525822338495686!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x60195e52526915f9%3A0x1a967cee111ec37d!2sMomiji%20Corridor%20Lake%20Kawaguchi!5e0!3m2!1szh-TW!2stw!4v1690639398445!5m2!1szh-TW!2stw",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),e[2]||(e[2]=a("h2",{id:"相關連結",tabindex:"-1"},[s("相關連結 "),a("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),a("ul",null,[a("li",null,[d(r,{url:"https://fujisan.ne.jp/event/info.php?if_id=404",placeHolderTitle:"富士河口湖觀光綜合網站 - 紅葉祭"})])])])}const k=i(m,[["render",h]]);export{b as __pageData,k as default};
