import{_ as i,I as t,c as o,o as s,a5 as l,j as a,a as n,J as d}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"日本 -> 山梨 -> 景點 -> 河口湖音樂盒之森美術館 (Kawaguchiko Music Forest Museum)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/山梨/景點/河口湖音樂盒之森美術館.md","filePath":"docs/日本/山梨/景點/河口湖音樂盒之森美術館.md","lastUpdated":1766900024000}'),u={name:"日本/山梨/景點/河口湖音樂盒之森美術館.md"};function m(c,e,h,g,_,p){const r=t("PreviewLink");return s(),o("div",null,[e[0]||(e[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/山梨/景點/河口湖音樂盒之森美術館.md for this page in Markdown format</div><h1 id="河口湖音樂盒之森美術館-kawaguchiko-music-forest-museum" tabindex="-1">河口湖音樂盒之森美術館 (Kawaguchiko Music Forest Museum) <a class="header-anchor" href="#河口湖音樂盒之森美術館-kawaguchiko-music-forest-museum" aria-label="Permalink to “河口湖音樂盒之森美術館 (Kawaguchiko Music Forest Museum)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/5/5d/Kawaguchiko_Music_Forest_Museum_2018a.jpg" alt="河口湖音樂盒之森美術館建築" loading="lazy"></p><p>河口湖音樂盒之森美術館是一座以中世紀歐洲王室建築為風格的美術館，園內充滿了異國風情，有著歐式庭園、玫瑰園，還能遠眺富士山。館內收藏了許多珍貴的自動演奏樂器及大型音樂盒，包括原本預定搭載於鐵達尼號上的自動演奏樂器。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>自動演奏樂器演奏</strong>：館內會定時舉辦自動演奏樂器的演奏會，令人震撼。</li><li><strong>歐式庭園</strong>：四季皆有不同的花卉，特別是玫瑰花季時非常美麗。</li><li><strong>富士山美景</strong>：從園內可以清楚地看到富士山與歐式建築交織的美景。</li><li><strong>沙畫表演</strong>：常有結合音樂與沙畫的精彩演出。</li></ul><h2 id="基本資訊" tabindex="-1">基本資訊 <a class="header-anchor" href="#基本資訊" aria-label="Permalink to “基本資訊”">​</a></h2><ul><li><strong>地址</strong>：〒401-0304 山梨県南都留郡富士河口湖町河口 3077-20</li><li><strong>電話</strong>：+81 555-20-4111</li><li><strong>營業時間</strong>：10:00 - 17:00 (最後入館 16:00)，具體請參考官網。</li><li><strong>門票</strong>：成人約 1,800 - 2,100 日圓 (價格依日期變動)。</li></ul><h2 id="交通方式" tabindex="-1">交通方式 <a class="header-anchor" href="#交通方式" aria-label="Permalink to “交通方式”">​</a></h2><ul><li>搭乘<strong>河口湖周遊巴士 (紅線)</strong> 至「河口湖音樂盒之森美術館」站下車即達。</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',11)),e[1]||(e[1]=a("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3244.661706173429!2d138.76101461525628!3d35.52243458022802!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x6019609f98725807%3A0xc3f5d179619b674!2sKawaguchiko%20Music%20Forest%20Museum!5e0!3m2!1szh-TW!2stw!4v1690638751215!5m2!1szh-TW!2stw",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),e[2]||(e[2]=a("h2",{id:"相關連結",tabindex:"-1"},[n("相關連結 "),a("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),a("ul",null,[a("li",null,[d(r,{url:"https://kawaguchikomusicforest.jp/",placeHolderTitle:"河口湖音樂盒之森美術館 官方網站"})])])])}const w=i(u,[["render",m]]);export{k as __pageData,w as default};
