import{_ as o,I as r,c as n,o as t,a5 as s,j as e,a as l,J as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 山梨 -> 景點 -> 西湖療癒之里根場 (Saiko Iyashi no Sato Nenba)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/山梨/景點/西湖療癒之里根場.md","filePath":"docs/日本/山梨/景點/西湖療癒之里根場.md","lastUpdated":1766900024000}'),h={name:"日本/山梨/景點/西湖療癒之里根場.md"};function m(c,a,p,f,_,g){const i=r("PreviewLink");return t(),n("div",null,[a[0]||(a[0]=s('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/山梨/景點/西湖療癒之里根場.md for this page in Markdown format</div><h1 id="西湖療癒之里根場-saiko-iyashi-no-sato-nenba" tabindex="-1">西湖療癒之里根場 (Saiko Iyashi no Sato Nenba) <a class="header-anchor" href="#西湖療癒之里根場-saiko-iyashi-no-sato-nenba" aria-label="Permalink to “西湖療癒之里根場 (Saiko Iyashi no Sato Nenba)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/c/ca/Saiko-iyashinosato-nenba2.JPG" alt="西湖療癒之里根場的合掌造村落" loading="lazy"></p><p>西湖療癒之里根場位於西湖北岸，重建了過去曾因颱風毀壞的合掌造村落。這裡有著二十多棟茅葺屋，背景是雄偉的富士山，彷彿穿越回日本古代的農村。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>合掌造建築</strong>：每棟房屋內都有不同的展覽或體驗，如傳統工藝、和服體驗、當地點心等。</li><li><strong>富士山取景</strong>：這裡是拍攝「合掌屋與富士山」經典畫面的絕佳地點。</li><li><strong>傳統體驗</strong>：可以穿著和服或盔甲在村落內散步拍照。</li></ul><h2 id="基本資訊" tabindex="-1">基本資訊 <a class="header-anchor" href="#基本資訊" aria-label="Permalink to “基本資訊”">​</a></h2><ul><li><strong>地址</strong>：〒401-0332 山梨県南都留郡富士河口湖町西湖根場 2710</li><li><strong>電話</strong>：+81 555-20-4677</li><li><strong>營業時間</strong>：09:00 - 17:00 (3 月 -11 月) / 09:30 - 16:30 (12 月 -2 月)。</li><li><strong>門票</strong>：成人約 500 日圓。</li></ul><h2 id="交通方式" tabindex="-1">交通方式 <a class="header-anchor" href="#交通方式" aria-label="Permalink to “交通方式”">​</a></h2><ul><li>搭乘<strong>西湖周遊巴士 (綠線)</strong> 至「西湖療癒之里根場」站下車即達。</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',11)),a[1]||(a[1]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3245.54924765664!2d138.66634731525565!3d35.50057298023348!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x60195ee0f3333333%3A0x6e6371c64d858348!2sSaiko%20Iyashi%20no%20Sato%20Nenba!5e0!3m2!1szh-TW!2stw!4v1690638751215!5m2!1szh-TW!2stw",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),a[2]||(a[2]=e("h2",{id:"相關連結",tabindex:"-1"},[l("相關連結 "),e("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),e("ul",null,[e("li",null,[d(i,{url:"https://www.fujisan.ne.jp/spot/info.php?if_id=531",placeHolderTitle:"富士河口湖觀光綜合網站 - 西湖療癒之里根場"})])])])}const k=o(h,[["render",m]]);export{b as __pageData,k as default};
