import{_ as t,I as i,c as l,o as n,a5 as o,j as e,a as s,J as d}from"./chunks/framework.BtqC3pLB.js";const w=JSON.parse('{"title":"日本 -> 山梨 -> 景點 -> 鳴澤冰穴 (Narusawa Ice Cave)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/山梨/景點/鳴澤冰穴.md","filePath":"docs/日本/山梨/景點/鳴澤冰穴.md","lastUpdated":1766900024000}'),c={name:"日本/山梨/景點/鳴澤冰穴.md"};function h(m,a,p,u,f,g){const r=i("PreviewLink");return n(),l("div",null,[a[0]||(a[0]=o('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/山梨/景點/鳴澤冰穴.md for this page in Markdown format</div><h1 id="鳴澤冰穴-narusawa-ice-cave" tabindex="-1">鳴澤冰穴 (Narusawa Ice Cave) <a class="header-anchor" href="#鳴澤冰穴-narusawa-ice-cave" aria-label="Permalink to “鳴澤冰穴 (Narusawa Ice Cave)”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/5/58/Narusawa_Hyoketsu_Entrance_20161025.jpg" alt="鳴澤冰穴入口" loading="lazy"></p><p>鳴澤冰穴是一個長度約 153 公尺的環狀溶岩洞穴。洞內終年結冰，即使在盛夏也能看到巨大的冰柱。洞穴高度較低，有些地方需要彎腰甚至爬行，充滿探險感。</p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>萬年冰柱</strong>：由上方滴落的水滴結成，場面壯觀。</li><li><strong>溶岩隧道</strong>：展示了富士山噴發後溶岩冷卻形成的特殊構造。</li><li><strong>探險體驗</strong>：洞內道路狹窄崎嶇，是一場短暫的地下冒險。</li></ul><h2 id="基本資訊" tabindex="-1">基本資訊 <a class="header-anchor" href="#基本資訊" aria-label="Permalink to “基本資訊”">​</a></h2><ul><li><strong>地址</strong>：〒401-0320 山梨県南都留郡鳴沢村 8533</li><li><strong>電話</strong>：+81 555-85-2301</li><li><strong>營業時間</strong>：09:00 - 17:00 (隨季節調整)。</li><li><strong>門票</strong>：成人約 350 日圓 (與富岳風穴有套票)。</li></ul><h2 id="交通方式" tabindex="-1">交通方式 <a class="header-anchor" href="#交通方式" aria-label="Permalink to “交通方式”">​</a></h2><ul><li>搭乘<strong>藍線巴士</strong> 至「鳴澤冰穴」站下車即達。 (綠線巴士亦可抵達附近的富岳風穴再步行前往)</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',11)),a[1]||(a[1]=e("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3246.34751765664!2d138.65834731525515!3d34.48107298023348!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x60195ee0f3333333%3A0x6e6371c64d858348!2sNarusawa%20Ice%20Cave!5e0!3m2!1szh-TW!2stw!4v1690638751215!5m2!1szh-TW!2stw",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),a[2]||(a[2]=e("h2",{id:"相關連結",tabindex:"-1"},[s("相關連結 "),e("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),e("ul",null,[e("li",null,[d(r,{url:"https://www.fujisan.ne.jp/spot/info.php?if_id=410",placeHolderTitle:"富士河口湖觀光綜合網站 - 鳴澤冰穴"})])])])}const b=t(c,[["render",h]]);export{w as __pageData,b as default};
