import{_ as o,I as l,c as n,o as i,a5 as s,j as a,a as d,J as t}from"./chunks/framework.BtqC3pLB.js";const w=JSON.parse('{"title":"日本 -> 岐阜 -> 景點 -> 下呂溫泉","description":"","frontmatter":{},"headers":[],"relativePath":"日本/岐阜/景點/下呂溫泉.md","filePath":"docs/日本/岐阜/景點/下呂溫泉.md","lastUpdated":1768235822000}'),h={name:"日本/岐阜/景點/下呂溫泉.md"};function g(p,e,c,m,u,f){const r=l("PreviewLink");return i(),n("div",null,[e[0]||(e[0]=s('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/岐阜/景點/下呂溫泉.md for this page in Markdown format</div><h1 id="下呂溫泉" tabindex="-1">下呂溫泉 <a class="header-anchor" href="#下呂溫泉" aria-label="Permalink to “下呂溫泉”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/3/32/Gero-onsen01s3200.jpg" alt="下呂溫泉" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><p>下呂溫泉與兵庫縣的有馬溫泉、群馬縣的草津溫泉並稱為「日本三大名泉」。自室町時代以來，這裡便以優質的泉質聞名，水質觸感滑順，具有「美人湯」的美譽。</p><h2 id="景點介紹" tabindex="-1">景點介紹 <a class="header-anchor" href="#景點介紹" aria-label="Permalink to “景點介紹”">​</a></h2><ul><li><strong>噴泉池：</strong> 位於益田川河床上的露天浴池，是下呂溫泉的象徵。遊客可以在大自然中體驗泡湯（需穿著泳衣）。</li><li><strong>下呂溫泉合掌村：</strong> 從<strong><a href="./白川鄉">白川鄉</a></strong>等地遷移過來的合掌造建築群，展示了山村的傳統生活與文化。</li><li><strong>溫泉寺：</strong> 供奉藥師如來的寺廟，傳說中藥師如來化身為受傷的白鷺，指引人們發現了溫泉。</li><li><strong>足湯巡禮：</strong> 溫泉街內設有多處免費足湯，遊客可以一邊漫步一邊輕鬆享受。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><h3 id="從名古屋出發" tabindex="-1">從名古屋出發 <a class="header-anchor" href="#從名古屋出發" aria-label="Permalink to “從名古屋出發”">​</a></h3><ul><li><strong>JR 特急 Wide View 飛驒：</strong> 車程約 1 小時 40 分鐘。可使用 <strong>[JR Pass](/日本/愛知/交通/JR Pass)</strong>。</li><li><strong>直通巴士：</strong> 名古屋車站有直達下呂溫泉的預約制接駁巴士。可參考 <strong><a href="https://www.gero-spa.or.jp/bus/" target="_blank" rel="noreferrer">下呂溫泉直行巴士官方網站</a></strong>。</li></ul><h3 id="從高山出發" tabindex="-1">從高山出發 <a class="header-anchor" href="#從高山出發" aria-label="Permalink to “從高山出發”">​</a></h3><ul><li><strong>JR 線：</strong> 搭乘特急約 45 分鐘，普通車約 1 小時 10 分鐘。</li></ul><h2 id="溫泉體驗" tabindex="-1">溫泉體驗 <a class="header-anchor" href="#溫泉體驗" aria-label="Permalink to “溫泉體驗”">​</a></h2><ul><li><strong>湯巡手形：</strong> 購買此木製票券後，可從參加活動的旅館中任選三家進入泡湯，是體驗不同泉質的好方法。</li><li><strong>日歸溫泉：</strong> 許多旅館與大眾浴場（如白鷺之湯）皆提供日歸泡湯服務。</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',14)),e[1]||(e[1]=a("iframe",{src:"https://www.google.com/maps?output=embed&q=Gero+Onsen",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),e[2]||(e[2]=a("h2",{id:"相關連結",tabindex:"-1"},[d("相關連結 "),a("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),a("ul",null,[a("li",null,[t(r,{url:"https://www.gero-spa.or.jp/hant/",placeHolderTitle:"下呂溫泉觀光協會"})]),a("li",null,[t(r,{url:"http://www.gero-gassho.jp/",placeHolderTitle:"下呂溫泉合掌村官方網站"})])])])}const b=o(h,[["render",g]]);export{w as __pageData,b as default};
