import{_ as l,I as i,c as o,o as n,a5 as s,j as e,a as d,J as t}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 岐阜 -> 景點 -> 岐阜城","description":"","frontmatter":{},"headers":[],"relativePath":"日本/岐阜/景點/岐阜城.md","filePath":"docs/日本/岐阜/景點/岐阜城.md","lastUpdated":1768235822000}'),h={name:"日本/岐阜/景點/岐阜城.md"};function c(m,a,p,g,u,f){const r=i("PreviewLink");return n(),o("div",null,[a[0]||(a[0]=s('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/岐阜/景點/岐阜城.md for this page in Markdown format</div><h1 id="岐阜城" tabindex="-1">岐阜城 <a class="header-anchor" href="#岐阜城" aria-label="Permalink to “岐阜城”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/9/9d/Gifu_castle.jpg" alt="岐阜城" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><p>岐阜城矗立於海拔 329 公尺的金華山山頂，是岐阜市的象徵。這裡曾是戰國大名織田信長「天下布武」的根據地，在歷史上具有極其重要的地位。</p><h2 id="景點介紹" tabindex="-1">景點介紹 <a class="header-anchor" href="#景點介紹" aria-label="Permalink to “景點介紹”">​</a></h2><ul><li><strong>天守閣：</strong> 現存的天守閣為重建建築，內部設有史料展示室，陳列武具與歷史文獻。從顶層展望台可以 360 度俯瞰濃尾平原、長良川以及遠方的阿爾卑斯山脈。</li><li><strong>金華山纜車：</strong> 連結岐阜公園與金華山山頂，航程約 3 分鐘，沿途可欣賞茂密的森林。</li><li><strong>岐阜公園：</strong> 位於山腳下，內有信長居館遺址及美麗的庭園。</li><li><strong>長良川鵜飼：</strong> 每年 5 月至 10 月，在城下的長良川會舉行傳統的鸕鶿捕魚活動，是擁有 1300 年歷史的無形文化遺產。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><h3 id="從名古屋出發" tabindex="-1">從名古屋出發 <a class="header-anchor" href="#從名古屋出發" aria-label="Permalink to “從名古屋出發”">​</a></h3><ul><li><strong>JR 或名鐵：</strong> 搭乘 JR 東海道線或名鐵名古屋線至「岐阜站」，車程約 20 分鐘。可參考 <strong>[JR Pass](/日本/愛知/交通/JR Pass)</strong> 或 <strong><a href="/日本/愛知/交通/昇龍道巴士">昇龍道巴士</a></strong>。</li></ul><h3 id="市內交通" tabindex="-1">市內交通 <a class="header-anchor" href="#市內交通" aria-label="Permalink to “市內交通”">​</a></h3><ul><li><strong>巴士：</strong> 從 JR 岐阜站或名鐵岐阜站搭乘往「岐阜公園、高富」方向的巴士，在「岐阜公園歷史博物館前」站下車。</li></ul><h2 id="旅遊建議" tabindex="-1">旅遊建議 <a class="header-anchor" href="#旅遊建議" aria-label="Permalink to “旅遊建議”">​</a></h2><ul><li><strong>夜景體驗：</strong> 夏季與特定週末，岐阜城與纜車會延長營業至晚間，這裡是日本著名的夜景勝地之一。</li><li><strong>登山步道：</strong> 金華山擁有多條難度不一的登山步道，適合喜愛健行的遊客。</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',14)),a[1]||(a[1]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=Gifu+Castle",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),a[2]||(a[2]=e("h2",{id:"相關連結",tabindex:"-1"},[d("相關連結 "),e("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),e("ul",null,[e("li",null,[t(r,{url:"https://www.gifucvb.or.jp/tch/01_sightseeing/01_01.html",placeHolderTitle:"岐阜觀光會議協會 - 岐阜城"})]),e("li",null,[t(r,{url:"http://www.kinkazan.co.jp/",placeHolderTitle:"金華山纜車官方網站"})])])])}const k=l(h,[["render",c]]);export{b as __pageData,k as default};
