import{_ as i,c as a,o as e,a3 as r}from"./chunks/framework.Coj2bmTX.js";const p=JSON.parse('{"title":"日本 -> 廣島 -> 景點 -> 千光寺","description":"","frontmatter":{},"headers":[],"relativePath":"日本/廣島/景點/千光寺.md","filePath":"docs/日本/廣島/景點/千光寺.md","lastUpdated":1742396662000}'),t={name:"日本/廣島/景點/千光寺.md"};function h(n,l,u,d,o,s){return e(),a("div",null,[...l[0]||(l[0]=[r('<h1 id="千光寺" tabindex="-1">千光寺 <a class="header-anchor" href="#千光寺" aria-label="Permalink to “千光寺”">​</a></h1><p>千光寺位於日本廣島縣尾道市東土堂町，坐落於千光寺山山腰，海拔約 144 公尺，是該市最著名的寺廟之一。</p><h2 id="主要景點" tabindex="-1">主要景點 <a class="header-anchor" href="#主要景點" aria-label="Permalink to “主要景點”">​</a></h2><ul><li><p>千光寺本堂：</p><ul><li>供奉千手觀音</li><li>為中國三十三觀音第十番札所</li><li>周圍有夫妻岩、鏡岩、玉岩等形狀奇特的岩石</li></ul></li><li><p>五重塔：</p><ul><li>建於 1972 年</li><li>夜晚會點燈</li><li>是尾道市的象徵之一</li></ul></li><li><p>展望台：</p><ul><li>位於千光寺公園內</li><li>可俯瞰尾道市區、尾道水道及對岸的向島</li><li>被選為「日本夜景遺產」</li><li>櫻花季節特別美麗</li></ul></li></ul><h2 id="交通方式" tabindex="-1">交通方式 <a class="header-anchor" href="#交通方式" aria-label="Permalink to “交通方式”">​</a></h2><h3 id="抵達纜車站" tabindex="-1">抵達纜車站 <a class="header-anchor" href="#抵達纜車站" aria-label="Permalink to “抵達纜車站”">​</a></h3><ol><li><p>從尾道站：</p><ul><li>徒步： <ul><li>往南步行約 15 分鐘可到達山麓纜車站</li><li>沿路有指示牌引導</li><li>經過商店街，可順路逛街</li></ul></li><li>巴士： <ul><li>在尾道站前搭乘「中央循環巴士」</li><li>在「千光寺山ロープウェイ」站下車</li><li>車程約 7 分鐘，票價約 200 日圓</li></ul></li><li>計程車： <ul><li>車程約 5 分鐘</li><li>費用約 700 日圓</li></ul></li></ul></li><li><p>纜車資訊：</p><ul><li>名稱：千光寺山ロープウェイ</li><li>運行時間：9:00-17:15（最後上山 16:45）</li><li>票價： <ul><li>單程：成人 320 日圓、兒童 160 日圓</li><li>來回：成人 540 日圓、兒童 270 日圓</li></ul></li><li>約 3 分鐘可到達山頂站</li></ul></li><li><p>徒步登山路線：</p><ul><li>從山麓纜車站旁有石階步道</li><li>途經天寧寺等古寺</li><li>約需 25-30 分鐘</li><li>坡度較陡，建議體力好的遊客選擇</li></ul></li></ol><h3 id="注意事項" tabindex="-1">注意事項 <a class="header-anchor" href="#注意事項" aria-label="Permalink to “注意事項”">​</a></h3><ul><li>纜車可能因強風或維修暫停運行</li><li>建議提前查詢當日運行狀況</li><li>旺季可能需要排隊等候</li><li>山路階梯較多，建議穿著舒適鞋子</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><p>開放時間：</p><ul><li>寺院：8:00-17:00</li><li>纜車：9:00-17:15</li><li>全年無休</li></ul></li><li><p>最佳參觀季節：</p><ul><li>春季：被選為日本櫻名所 100 選（3 月下旬 -4 月上旬）</li><li>秋季：楓葉轉紅（11 月中旬 -12 月上旬）</li><li>夜景：日落後至 20:00</li></ul></li></ul><h2 id="周邊景點" tabindex="-1">周邊景點 <a class="header-anchor" href="#周邊景點" aria-label="Permalink to “周邊景點”">​</a></h2><ul><li>文學之路：展示與尾道相關的文學作品</li><li>貓之細道：以貓咪聞名的小徑</li><li>天寧寺：途中必經的古寺</li><li>尾道商店街：可品嚐當地美食</li></ul><h2 id="注意事項-1" tabindex="-1">注意事項 <a class="header-anchor" href="#注意事項-1" aria-label="Permalink to “注意事項”">​</a></h2><ul><li>建議穿著舒適的步行鞋</li><li>夏季請攜帶防曬用品和飲用水</li><li>雨天台階可能會濕滑，要特別小心</li><li>建議預留 2-3 小時遊覽時間</li></ul>',15)])])}const _=i(t,[["render",h]]);export{p as __pageData,_ as default};
