import{_ as l,c as e,o as i,a3 as r}from"./chunks/framework.Coj2bmTX.js";const p=JSON.parse('{"title":"日本 -> 廣島 -> 景點 -> 原爆圓頂館","description":"","frontmatter":{},"headers":[],"relativePath":"日本/廣島/景點/原爆圓頂館.md","filePath":"docs/日本/廣島/景點/原爆圓頂館.md","lastUpdated":1742396662000}'),t={name:"日本/廣島/景點/原爆圓頂館.md"};function n(h,a,d,o,s,_){return i(),e("div",null,[...a[0]||(a[0]=[r('<h1 id="原爆圓頂館" tabindex="-1">原爆圓頂館 <a class="header-anchor" href="#原爆圓頂館" aria-label="Permalink to “原爆圓頂館”">​</a></h1><p>原爆圓頂館（原爆ドーム）是廣島和平紀念碑的一部分，也是世界文化遺產，是廣島原子彈爆炸的象徵性建築物，也是少數在爆炸中倖存的建築物之一。</p><h2 id="基本資訊" tabindex="-1">基本資訊 <a class="header-anchor" href="#基本資訊" aria-label="Permalink to “基本資訊”">​</a></h2><ul><li>地址：広島県広島市中区大手町 1-10</li><li>營業時間：24 小時可從外部觀看（無法進入內部）</li><li>門票：免費</li><li>開放時間：24 小時</li><li>交通： <ul><li>廣島電鐵「原爆ドーム前」站下車，步行約 1 分鐘</li><li>廣島巴士「平和記念公園」站下車，步行約 5 分鐘</li></ul></li></ul><h2 id="景點介紹" tabindex="-1">景點介紹 <a class="header-anchor" href="#景點介紹" aria-label="Permalink to “景點介紹”">​</a></h2><p>原爆圓頂館原本是廣島縣產業獎勵館，建於 1915 年，是由捷克建築師揚·萊策爾設計的。1945 年 8 月 6 日，美國在廣島投下原子彈，爆炸點位於建築物上方約 160 公尺處，建築物雖然遭受嚴重損壞，但因為爆炸點幾乎位於正上方，建築物的部分牆壁和圓頂結構得以保存下來。</p><p>1996 年，原爆圓頂館被列入世界文化遺產名錄，成為人類歷史上重要的和平象徵。</p><h2 id="推薦活動" tabindex="-1">推薦活動 <a class="header-anchor" href="#推薦活動" aria-label="Permalink to “推薦活動”">​</a></h2><ul><li>參觀原爆圓頂館外觀</li><li>在夜間觀賞點燈後的原爆圓頂館</li><li>參觀附近的和平紀念公園</li><li>參觀廣島和平紀念資料館</li></ul><h2 id="注意事項" tabindex="-1">注意事項 <a class="header-anchor" href="#注意事項" aria-label="Permalink to “注意事項”">​</a></h2><ul><li>原爆圓頂館是一個紀念場所，請保持安靜，尊重逝者</li><li>為了保存建築物，遊客不能進入建築物內部</li><li>建議與和平紀念公園一同參觀</li></ul><h2 id="資料來源" tabindex="-1">資料來源 <a class="header-anchor" href="#資料來源" aria-label="Permalink to “資料來源”">​</a></h2><ul><li><a href="https://zh.wikipedia.org/wiki/%E5%8E%9F%E7%88%86%E5%9C%93%E9%A0%82%E9%A4%A8" target="_blank" rel="noreferrer">維基百科 - 原爆圓頂館</a></li></ul>',13)])])}const f=l(t,[["render",n]]);export{p as __pageData,f as default};
