import{_ as i,c as a,o as e,a3 as r}from"./chunks/framework.Coj2bmTX.js";const h=JSON.parse('{"title":"日本 -> 廣島 -> 景點 -> 和平紀念公園","description":"","frontmatter":{},"headers":[],"relativePath":"日本/廣島/景點/和平紀念公園.md","filePath":"docs/日本/廣島/景點/和平紀念公園.md","lastUpdated":1742396560000}'),t={name:"日本/廣島/景點/和平紀念公園.md"};function o(n,l,s,_,d,u){return e(),a("div",null,[...l[0]||(l[0]=[r('<h1 id="和平紀念公園" tabindex="-1">和平紀念公園 <a class="header-anchor" href="#和平紀念公園" aria-label="Permalink to “和平紀念公園”">​</a></h1><p>和平紀念公園位於廣島市中心，是為紀念原子彈爆炸遇難者而建造的和平紀念設施。公園內包含多個重要建築和紀念碑，是世界文化遺產之一。</p><h2 id="主要景點" tabindex="-1">主要景點 <a class="header-anchor" href="#主要景點" aria-label="Permalink to “主要景點”">​</a></h2><ul><li>原爆圓頂館（A-Bomb Dome）： <ul><li>原子彈爆炸時倖存的建築物</li><li>被列為世界文化遺產</li><li>象徵和平的重要地標</li></ul></li><li>和平紀念資料館： <ul><li>展示原爆相關歷史資料</li><li>介紹廣島的重建歷程</li><li>推廣世界和平理念</li></ul></li><li>和平紀念碑： <ul><li>刻有原爆遇難者名冊</li><li>每年 8 月 6 日舉行追悼儀式</li></ul></li></ul><h2 id="交通" tabindex="-1">交通 <a class="header-anchor" href="#交通" aria-label="Permalink to “交通”">​</a></h2><ul><li>從廣島站： <ul><li>搭乘路面電車 2 號或 6 號線至「原爆ドーム前」站</li><li>車程約 15 分鐘</li></ul></li><li>從廣島機場： <ul><li>搭乘機場巴士至廣島站後轉乘路面電車</li></ul></li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li>公園：全天 24 小時開放，免費</li><li>和平紀念資料館： <ul><li>開放時間：8:30-18:00（3-7 月延長至 19:00）</li><li>門票：成人 200 日圓，高中生以下免費</li><li>每週四定期舉辦英語導覽</li></ul></li></ul>',8)])])}const m=i(t,[["render",o]]);export{h as __pageData,m as default};
