import{_ as t,I as r,c as o,o as n,a5 as d,j as e,J as i}from"./chunks/framework.BtqC3pLB.js";const P=JSON.parse('{"title":"日本 -> 廣島 -> 景點 -> 廣島城","description":"","frontmatter":{},"headers":[],"relativePath":"日本/廣島/景點/廣島城.md","filePath":"docs/日本/廣島/景點/廣島城.md","lastUpdated":1742396662000}'),s={name:"日本/廣島/景點/廣島城.md"};function c(h,a,p,m,u,_){const l=r("PreviewLink");return n(),o("div",null,[a[0]||(a[0]=d('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/廣島/景點/廣島城.md for this page in Markdown format</div><h1 id="廣島城" tabindex="-1">廣島城 <a class="header-anchor" href="#廣島城" aria-label="Permalink to “廣島城”">​</a></h1><p>廣島城是位於日本廣島縣廣島市中區的一座日本城堡，建於 1589 年。原城在 1945 年因原子彈爆炸而被摧毀，現在的建築是 1958 年重建的。城堡外觀為 5 層樓高，內部設有博物館，展示廣島的歷史文物。</p><p>從城天守閣可以俯瞰廣島市區景色，是廣島市重要的觀光景點之一。城堡周圍有護城河和廣大的公園綠地，春季可以欣賞櫻花。</p><h2 id="交通" tabindex="-1">交通 <a class="header-anchor" href="#交通" aria-label="Permalink to “交通”">​</a></h2><ul><li>從廣島站搭乘路面電車： <ul><li>搭乘 2 號或 6 號線至「紙屋町西」站下車，步行約 10 分鐘</li><li>搭乘 1 號、2 號或 6 號線至「中電前」站下車，步行約 10 分鐘</li></ul></li><li>從廣島機場：搭乘機場巴士至廣島站，再轉乘路面電車</li><li>從和平紀念公園：步行約 15 分鐘即可到達</li></ul><h2 id="營業時間" tabindex="-1">營業時間 <a class="header-anchor" href="#營業時間" aria-label="Permalink to “營業時間”">​</a></h2><ul><li>開放時間：9:00-17:00（最後入場 16:30）</li><li>全年開放，無休館日</li><li>門票費用： <ul><li>成人：370 日圓</li><li>高中生：180 日圓</li><li>小學生/中學生：120 日圓</li><li>6 歲以下免費</li></ul></li></ul><h2 id="資料來源" tabindex="-1">資料來源 <a class="header-anchor" href="#資料來源" aria-label="Permalink to “資料來源”">​</a></h2>',9)),e("ul",null,[e("li",null,[i(l,{url:"https://www.japan-guide.com/e/e3402.html",placeHolderTitle:"Japan-guide.com - 廣島城"})]),e("li",null,[i(l,{url:"https://dive-hiroshima.com/en/explore/1173/",placeHolderTitle:"Dive-hiroshima.com - 廣島城"})])])])}const b=t(s,[["render",c]]);export{P as __pageData,b as default};
