import{_ as e,c as r,o as i,a5 as t}from"./chunks/framework.BtqC3pLB.js";const f=JSON.parse('{"title":"日本 -> 愛媛 -> 愛媛","description":"","frontmatter":{},"headers":[],"relativePath":"日本/愛媛/index.md","filePath":"docs/日本/愛媛/index.md","lastUpdated":1766646134000}'),n={name:"日本/愛媛/index.md"};function l(o,a,d,s,h,c){return i(),r("div",null,[...a[0]||(a[0]=[t('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/愛媛.md for this page in Markdown format</div><h1 id="愛媛" tabindex="-1">愛媛 <a class="header-anchor" href="#愛媛" aria-label="Permalink to “愛媛”">​</a></h1><h2 id="柑橘之鄉與文學之都" tabindex="-1">柑橘之鄉與文學之都 <a class="header-anchor" href="#柑橘之鄉與文學之都" aria-label="Permalink to “柑橘之鄉與文學之都”">​</a></h2><p>愛媛縣位於四國地區西北部，以其溫暖的氣候、豐富的自然資源和悠久的歷史文化而聞名。這裡盛產柑橘，因此有「柑橘之鄉」的美譽。同時，愛媛縣也是日本文學的重要發源地，孕育了許多著名的文學家。</p><h2 id="必訪景點" tabindex="-1">必訪景點 <a class="header-anchor" href="#必訪景點" aria-label="Permalink to “必訪景點”">​</a></h2><ul><li><strong><a href="./景點/道後溫泉">道後溫泉</a>：</strong> 三千年歷史，日本最古老溫泉。</li><li><strong><a href="./景點/松山城">松山城</a>：</strong> 現存十二天守之一。</li><li><strong>宇和島城</strong>：獨特建築風格的現存天守（內容編寫中）。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><ul><li><strong><a href="./交通/">交通概覽</a>：</strong> 包含松山機場及縣內鐵路、路面電車資訊。</li></ul><h2 id="溫馨提示" tabindex="-1">溫馨提示 <a class="header-anchor" href="#溫馨提示" aria-label="Permalink to “溫馨提示”">​</a></h2><ul><li>前往道後溫泉時，可以體驗傳統的「朝湯」，感受當地人的生活。</li><li>在松山城可以搭乘復古纜車「少爺列車」登山，體驗懷舊的氛圍。</li><li>愛媛縣以其美味的柑橘和海鮮而聞名。來到這裡，不妨品嚐一下新鮮多汁的柑橘，以及鯛魚和海膽等海鮮料理。</li></ul>',10)])])}const m=e(n,[["render",l]]);export{f as __pageData,m as default};
