import{_ as l,c as i,o as e,a3 as r}from"./chunks/framework.Coj2bmTX.js";const m=JSON.parse('{"title":"日本 -> 愛媛 -> 景點 -> 松山城","description":"","frontmatter":{},"headers":[],"relativePath":"日本/愛媛/景點/松山城.md","filePath":"docs/日本/愛媛/景點/松山城.md","lastUpdated":1742621693000}'),t={name:"日本/愛媛/景點/松山城.md"};function n(s,a,o,d,h,_){return e(),i("div",null,[...a[0]||(a[0]=[r('<h1 id="松山城" tabindex="-1">松山城 <a class="header-anchor" href="#松山城" aria-label="Permalink to “松山城”">​</a></h1><h2 id="基本資訊" tabindex="-1">基本資訊 <a class="header-anchor" href="#基本資訊" aria-label="Permalink to “基本資訊”">​</a></h2><ul><li><strong>地址</strong>：愛媛県松山市丸之内 1</li><li><strong>營業時間</strong>：09:00~17:00（最終入館时间为 16:30）</li><li><strong>天守閣入場券</strong>：大人 520 日圓、兒童（小學生）160 日圓</li><li><strong>纜車</strong>：單程 大人 270 日圓、兒童 140 日圓，來回 大人 520 日圓、兒童 260 日圓</li><li>官方網站：<a href="https://www.matsuyamajo.jp" target="_blank" rel="noreferrer">松山城官方網站</a></li></ul><h2 id="特色" tabindex="-1">特色 <a class="header-anchor" href="#特色" aria-label="Permalink to “特色”">​</a></h2><ul><li>日本现存 12 座原貌天守之一</li><li>坐落在勝山山頂的連立式天守建築</li><li>可搭乘復古纜車「少爺列車」登山（单程 200 日圆，往返 350 日圆）</li><li>頂層展望台可眺望瀨戶內海風景</li><li>松山城是日本 12 座江戶時代以前建造的原始城堡之一。</li><li>松山城也被稱為金亀城 (Kinkijō) 和 勝山城 (Katsuyamajō)。</li><li>松山城建於 1602 年，歷時約四分之一世紀才完工。</li><li>松山城位於海拔 132 公尺的勝山山頂。</li><li>松山城是國家指定的歷史遺址。</li><li>從城堡塔樓看到的景色已被米其林綠色指南選為一星級。</li></ul><h2 id="推薦季節" tabindex="-1">推薦季節 <a class="header-anchor" href="#推薦季節" aria-label="Permalink to “推薦季節”">​</a></h2><ul><li>春季：櫻花季夜間點燈（推荐 3 月底至 4 月初）</li><li>秋季：山麓紅葉與天守相映（推荐 11 月上旬）</li><li>冬季：雪化妝天守景觀（推荐 12 月至 1 月）</li></ul><h2 id="交通方式" tabindex="-1">交通方式 <a class="header-anchor" href="#交通方式" aria-label="Permalink to “交通方式”">​</a></h2><ul><li>伊予鐵道「大街道站」步行 15 分鐘</li><li>市內電車「市役所前站」步行 10 分鐘</li><li>提供付費登山纜車「ロープウェー」</li></ul>',9)])])}const f=l(t,[["render",n]]);export{m as __pageData,f as default};
