import{_ as l,I as t,c as i,o as n,a5 as o,j as a,J as r}from"./chunks/framework.BtqC3pLB.js";const g=JSON.parse('{"title":"日本 -> 愛知 -> 交通 -> JR Pass (日本鐵路通票)","description":"","frontmatter":{},"headers":[],"relativePath":"日本/愛知/交通/JR Pass.md","filePath":"docs/日本/愛知/_交通/JR Pass.md","lastUpdated":1768235822000}'),h={name:"日本/愛知/交通/JR Pass.md"};function p(d,s,u,f,c,m){const e=t("PreviewLink");return n(),i("div",null,[s[0]||(s[0]=o('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/愛知/交通/JR Pass.md for this page in Markdown format</div><h1 id="jr-pass-日本鐵路通票" tabindex="-1">JR Pass (日本鐵路通票) <a class="header-anchor" href="#jr-pass-日本鐵路通票" aria-label="Permalink to “JR Pass (日本鐵路通票)”">​</a></h1><p>日本鐵路通票 (JR Pass) 是由 JR 集團 6 家公司共同提供的交通票券，適合搭乘鐵道橫跨全日本或特定區域旅行。</p><h2 id="全日本鐵路通票-national-jr-pass" tabindex="-1">全日本鐵路通票 (National JR Pass) <a class="header-anchor" href="#全日本鐵路通票-national-jr-pass" aria-label="Permalink to “全日本鐵路通票 (National JR Pass)”">​</a></h2><p>適合在短時間內長距離移動（例如東京－大阪－福岡）的旅客。</p><ul><li><strong>類型：</strong> 7 日、14 日、21 日券。</li><li><strong>適用範圍：</strong> 全日本的 JR 鐵路、部分 JR 巴士及宮島渡輪。</li><li><strong>注意事項：</strong> 搭乘「希望號 (Nozomi)」及「瑞穗號 (Mizuho)」需額外加購特急券。</li></ul><hr><h2 id="區域性周遊券-regional-passes" tabindex="-1">區域性周遊券 (Regional Passes) <a class="header-anchor" href="#區域性周遊券-regional-passes" aria-label="Permalink to “區域性周遊券 (Regional Passes)”">​</a></h2><p>針對特定地區旅遊，JR 也推出了許多價格更實惠的區域通票：</p><h3 id="高山、北陸地區周遊券-takayama-hokuriku-area-tourist-pass" tabindex="-1">高山、北陸地區周遊券 (Takayama-Hokuriku Area Tourist Pass) <a class="header-anchor" href="#高山、北陸地區周遊券-takayama-hokuriku-area-tourist-pass" aria-label="Permalink to “高山、北陸地區周遊券 (Takayama-Hokuriku Area Tourist Pass)”">​</a></h3><p>最適合計畫從<a href="./../../愛知/">名古屋</a>或<a href="./../../大阪/">大阪</a>出發，前往<a href="./../../岐阜/景點/下呂溫泉">下呂</a>、<a href="./../../岐阜/景點/高山老街">高山</a>、<a href="./../../岐阜/景點/白川鄉">白川鄉</a>及<a href="./../../石川/">金澤</a>、<a href="./../../富山/">富山</a>的旅客。</p><ul><li><strong>價格 (2026 年)：</strong> 成人 19,800 日圓 / 兒童 9,900 日圓</li><li><strong>有效期：</strong> 連續 5 日</li><li><strong>適用範圍：</strong><ul><li><strong>JR 鐵路：</strong> <a href="./../../愛知/">名古屋</a>－<a href="./../../岐阜/景點/下呂溫泉">下呂</a>－<a href="./../../岐阜/景點/高山老街">高山</a>－<a href="./../../富山/">富山</a>；<a href="./../../大阪/">大阪</a>－<a href="./../../京都/">京都</a>－<a href="./../../石川/">金澤</a>－<a href="./../../富山/">富山</a>。</li><li><strong>北陸新幹線：</strong> <a href="./../../石川/">金澤</a>－<a href="./../../富山/">富山</a>－<a href="./../../福井/">敦賀</a>區間。</li><li><strong>巴士：</strong> 濃飛巴士、北鐵巴士（往返<a href="./../../岐阜/景點/高山老街">高山</a>－<a href="./../../岐阜/景點/白川鄉">白川鄉</a>－<a href="./../../石川/">金澤</a>）。</li></ul></li><li><strong>預約次數：</strong> 可免費預約 JR 指定席最多 6 次。</li></ul><h3 id="關西、北陸地區周遊券" tabindex="-1">關西、北陸地區周遊券 <a class="header-anchor" href="#關西、北陸地區周遊券" aria-label="Permalink to “關西、北陸地區周遊券”">​</a></h3><p>適合從<a href="./../../大阪/">大阪</a>、<a href="./../../京都/">京都</a>出發，玩遍<a href="./../../滋賀/">滋賀</a>、<a href="./../../福井/">敦賀</a>、<a href="./../../石川/">金澤</a>、<a href="./../../富山/">富山</a>及岡山、和歌山的旅客。</p><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',15)),a("ul",null,[a("li",null,[a("p",null,[r(e,{url:"https://japanrailpass.net/zh/",placeHolderTitle:"JR Pass 官方網站"})])]),a("li",null,[a("p",null,[r(e,{url:"https://www.jreast.co.jp/multi/zh-CHT/index.html",placeHolderTitle:"JR 東日本 (JR East) 官網"})])]),a("li",null,[a("p",null,[r(e,{url:"https://jr-central.co.jp/hans/",placeHolderTitle:"JR 東海 (JR Central) 官網"})])]),a("li",null,[a("p",null,[r(e,{url:"https://www.westjr.co.jp/global/tc/",placeHolderTitle:"JR 西日本 (JR West) 官網"})])]),a("li",null,[a("p",null,[r(e,{url:"https://touristpass.jp/zh-tw/takayama_hokuriku/",placeHolderTitle:"高山、北陸地區周遊券官方網站"})])])])])}const R=l(h,[["render",p]]);export{g as __pageData,R as default};
