import{_ as t,I as o,c as n,o as s,a5 as d,j as a,J as l,a as i}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"日本 -> 福島 -> 景點 -> 大內宿","description":"","frontmatter":{},"headers":[],"relativePath":"日本/福島/景點/大內宿.md","filePath":"docs/日本/福島/景點/大內宿.md","lastUpdated":1768235822000}'),h={name:"日本/福島/景點/大內宿.md"};function u(m,e,p,c,g,f){const r=o("PreviewLink");return s(),n("div",null,[e[0]||(e[0]=d('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/福島/景點/大內宿.md for this page in Markdown format</div><h1 id="大內宿" tabindex="-1">大內宿 <a class="header-anchor" href="#大內宿" aria-label="Permalink to “大內宿”">​</a></h1><p>大內宿位於福島縣南會津郡下鄉町，是江戶時代連接會津若松與日光的重要驛站（宿場町）。這裡至今仍保留著 30 多座茅草屋頂的傳統建築，被指定為日本國家重要傳統建築物群保存地區。</p><h2 id="日本三大合掌造集落" tabindex="-1">日本三大合掌造集落 <a class="header-anchor" href="#日本三大合掌造集落" aria-label="Permalink to “日本三大合掌造集落”">​</a></h2><p>大內宿與以下地點並列為日本三大合掌造集落：</p><ul><li><strong><a href="./../../岐阜/景點/白川鄉">白川鄉</a>：</strong> 位於岐阜縣，最知名的世界遺產。</li><li><strong><a href="./../../富山/景點/五箇山">五箇山合掌集落</a>：</strong> 位於富山縣，氛圍較為淳樸。</li><li><strong><a href="./大內宿">大內宿</a>：</strong> 以江戶時代宿場町風情與大蔥蕎麥麵聞名。</li></ul><p><img src="https://upload.wikimedia.org/wikipedia/commons/e/e3/Ouchijuku_2006-11-12.jpg" alt="大內宿" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><p>走在寬闊的街道上，兩旁是古色古香的茅草屋，讓人彷彿回到了江戶時代。這裡的建築現在多作為土產店、餐廳或民宿經營。</p><h2 id="門票資訊" tabindex="-1">門票資訊 <a class="header-anchor" href="#門票資訊" aria-label="Permalink to “門票資訊”">​</a></h2><ul><li><strong>聚落參觀：</strong> 免費。</li><li><strong>大內宿町展覽館：</strong> 成人 250 日圓，中小學生 150 日圓。</li></ul><h2 id="必吃美食" tabindex="-1">必吃美食 <a class="header-anchor" href="#必吃美食" aria-label="Permalink to “必吃美食”">​</a></h2><ul><li><strong>高遠蕎麥麵（大蔥蕎麥麵）：</strong> 這裡最著名的美食。吃法非常獨特，不使用筷子，而是用一整根大蔥撈起麵條來吃，並同時咬一口大蔥作為辛香料。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><h3 id="大眾運輸" tabindex="-1">大眾運輸 <a class="header-anchor" href="#大眾運輸" aria-label="Permalink to “大眾運輸”">​</a></h3><ol><li><strong>搭乘新幹線：</strong> 至「郡山站」。</li><li><strong>轉乘 JR 磐越西線：</strong> 至「會津若松站」。</li><li><strong>轉乘會津鐵道：</strong> 至「湯野上溫泉站」。</li><li><strong>搭乘巴士：</strong> 從湯野上溫泉站搭乘觀光巴士「猿遊號」（猿游号）前往大內宿（車程約 20 分鐘）。 <ul><li><strong>建議：</strong> 可購買「大內宿共通套票」（約 2,200 日圓），包含鐵道與巴士費用。詳細資訊請參考 <strong><a href="https://ouchi-juku.com/index.php?Saruyu" target="_blank" rel="noreferrer">猿遊號觀光巴士官方網站</a></strong>。可使用 <strong>[JR Pass](/日本/愛知/交通/JR Pass)</strong> 抵達會津若松。</li></ul></li></ol><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',17)),a("ul",null,[a("li",null,[l(r,{url:"https://ouchi-juku.com/",placeHolderTitle:"大內宿觀光協會"})]),a("li",null,[l(r,{url:"https://shimogo.jp/",placeHolderTitle:"下鄉町觀光指南"})])]),e[1]||(e[1]=a("h2",{id:"溫馨提示",tabindex:"-1"},[i("溫馨提示 "),a("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),e[2]||(e[2]=a("ul",null,[a("li",null,"冬季的大內宿覆蓋著厚厚的白雪，景色非常夢幻，每年 2 月還會舉辦雪祭。"),a("li",null,"建議在早上或傍晚遊客較少時前往，更能感受古鎮的寧靜氛圍。")],-1)),e[3]||(e[3]=a("h2",{id:"地圖",tabindex:"-1"},[i("地圖 "),a("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),e[4]||(e[4]=a("iframe",{src:"https://www.google.com/maps?output=embed&q=Ouchi-juku",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const _=t(h,[["render",u]]);export{k as __pageData,_ as default};
