import{_ as t,I as i,c as o,o as n,a5 as l,j as e,a as s,J as d}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"日本 -> 長野 -> 景點 -> 上高地","description":"","frontmatter":{},"headers":[],"relativePath":"日本/長野/景點/上高地.md","filePath":"docs/日本/長野/景點/上高地.md","lastUpdated":1768235822000}'),h={name:"日本/長野/景點/上高地.md"};function c(m,a,p,g,u,_){const r=i("PreviewLink");return n(),o("div",null,[a[0]||(a[0]=l('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/長野/景點/上高地.md for this page in Markdown format</div><h1 id="上高地" tabindex="-1">上高地 <a class="header-anchor" href="#上高地" aria-label="Permalink to “上高地”">​</a></h1><p><img src="https://upload.wikimedia.org/wikipedia/commons/e/e0/Kamikochi_and_Azusa_River_in_Autumn.jpg" alt="上高地" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><p>上高地標高約 1500 公尺，位於長野縣西部的飛驒山脈（北阿爾卑斯）谷地。這裡被譽為「日本的阿爾卑斯」，是國家特別名勝與特別天然紀念物。</p><h2 id="景點精華" tabindex="-1">景點精華 <a class="header-anchor" href="#景點精華" aria-label="Permalink to “景點精華”">​</a></h2><ul><li><strong>河童橋：</strong> 上高地的象徵，是一座橫跨梓川的木造吊橋，在此可眺望穗高連峰與燒岳。</li><li><strong>大正池：</strong> 因 1915 年燒岳噴發阻斷梓川而形成的湖泊，湖中矗立著枯木，景致神祕。</li><li><strong>明神池：</strong> 位於穗高神社奧宮內，湖水清澈，是極具靈氣的地點。</li><li><strong>梓川水邊：</strong> 沿著清澈見底的梓川健行，呼吸山間的新鮮空氣。</li></ul><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><p>上高地實施全年的<strong>私家車管制</strong>，遊客需將車輛停放在停車場（如澤渡或平湯）後轉乘專用巴士或計程車。</p><ul><li><strong>從<a href="./../index">松本</a>出發：</strong> 搭乘松本電鐵上高地線至「新島島站」，轉乘巴士抵達。詳細資訊請參考 <strong><a href="https://www.alpico.co.jp/traffic/kamikochi/" target="_blank" rel="noreferrer">Alpico 巴士官方網站</a></strong>。</li><li><strong>從<a href="./../../岐阜/景點/奧飛驒溫泉鄉">平湯溫泉</a>出發：</strong> 搭乘穿梭巴士約 25 分鐘。可使用 <strong><a href="/日本/愛知/交通/昇龍道巴士">昇龍道巴士</a></strong>。</li></ul><h2 id="旅遊建議" tabindex="-1">旅遊建議 <a class="header-anchor" href="#旅遊建議" aria-label="Permalink to “旅遊建議”">​</a></h2><ul><li><strong>開山時間：</strong> 每年僅於 4 月中旬至 11 月中旬開放，冬季期間封山。</li><li><strong>氣候注意：</strong> 即使是夏季，山間氣溫也較低，建議備妥長袖衣物與防雨裝備。</li></ul><h2 id="地圖" tabindex="-1">地圖 <a class="header-anchor" href="#地圖" aria-label="Permalink to “地圖”">​</a></h2>',12)),a[1]||(a[1]=e("iframe",{src:"https://www.google.com/maps?output=embed&q=Kamikochi",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1)),a[2]||(a[2]=e("h2",{id:"相關連結",tabindex:"-1"},[s("相關連結 "),e("a",{class:"header-anchor",href:"#相關連結","aria-label":"Permalink to “相關連結”"},"​")],-1)),e("ul",null,[e("li",null,[d(r,{url:"https://www.kamikochi.or.jp/tw/",placeHolderTitle:"上高地官方網站"})])])])}const w=t(h,[["render",c]]);export{k as __pageData,w as default};
