import{_ as t,I as l,c as n,o,a5 as i,j as r,J as s}from"./chunks/framework.BtqC3pLB.js";const k=JSON.parse('{"title":"日本 -> 青森 -> 交通 -> 青森渡輪交通指南","description":"","frontmatter":{},"headers":[],"relativePath":"日本/青森/交通/渡輪.md","filePath":"docs/日本/青森/_交通/渡輪.md","lastUpdated":1766946043000}'),d={name:"日本/青森/交通/渡輪.md"};function h(c,a,g,p,u,_){const e=l("PreviewLink");return o(),n("div",null,[a[0]||(a[0]=i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/青森/交通/渡輪.md for this page in Markdown format</div><h1 id="青森渡輪交通指南" tabindex="-1">青森渡輪交通指南 <a class="header-anchor" href="#青森渡輪交通指南" aria-label="Permalink to “青森渡輪交通指南”">​</a></h1><p>青森是本州與北海道之間海運的樞紐，提供多條航線，特別適合自駕或是想要體驗船旅的遊客。</p><h2 id="青函航線-青森-↔-函館" tabindex="-1">青函航線 (青森 ↔ 函館) <a class="header-anchor" href="#青函航線-青森-↔-函館" aria-label="Permalink to “青函航線 (青森 ↔ 函館)”">​</a></h2><p>這是最繁忙的航線，連接青森市與北海道函館市。</p><ul><li><strong>起訖點：青森港 ↔ <a href="./../../北海道/交通/渡輪">函館港</a></strong></li><li><strong>營運公司</strong>： <ul><li><strong><a href="https://www.tsugarukaikyo.co.jp/" target="_blank" rel="noreferrer">津輕海峽渡輪 (Tsugaru Kaikyo Ferry)</a></strong>：船隻較新且大，設有舒適的休息區與寵物友善空間。</li><li><strong><a href="https://www.seikan-ferry.co.jp/" target="_blank" rel="noreferrer">青函渡輪 (Seikan Ferry)</a></strong>：價格較為實惠，班次密集。</li></ul></li><li><strong>所需時間</strong>：約 3 小時 40 分鐘。</li><li><strong>特色</strong>：24 小時皆有班次，可載運自用小客車。</li></ul><h2 id="大函航線-大間-↔-函館" tabindex="-1">大函航線 (大間 ↔ 函館) <a class="header-anchor" href="#大函航線-大間-↔-函館" aria-label="Permalink to “大函航線 (大間 ↔ 函館)”">​</a></h2><p>這是連接本州最北端「大間町」與函館的最短航線。</p><ul><li><strong>起訖點：大間港 ↔ <a href="./../../北海道/交通/渡輪">函館港</a></strong></li><li><strong>營運公司</strong>：<a href="https://www.tsugarukaikyo.co.jp/" target="_blank" rel="noreferrer">津輕海峽渡輪</a></li><li><strong>所需時間</strong>：約 90 分鐘。</li><li><strong>特色</strong>：航程最短，是從下北半島前往北海道最快的方式。</li></ul><h2 id="相關景點" tabindex="-1">相關景點 <a class="header-anchor" href="#相關景點" aria-label="Permalink to “相關景點”">​</a></h2><ul><li><strong><a href="./../../北海道/函館/">函館</a></strong>：抵達北海道後的首站。</li><li><strong>大間町</strong>：以「大間鮪魚」聞名。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',12)),r("ul",null,[r("li",null,[s(e,{url:"https://www.tsugarukaikyo.co.jp/global/cnt/",placeHolderTitle:"津輕海峽渡輪官方網站 (繁體中文)"})])])])}const m=t(d,[["render",h]]);export{k as __pageData,m as default};
