import{_ as o,I as i,c as n,o as s,a5 as d,j as a,J as t,a as r}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 青森 -> 青森 -> 景點 -> 酸湯溫泉","description":"","frontmatter":{},"headers":[],"relativePath":"日本/青森/青森/景點/酸湯溫泉.md","filePath":"docs/日本/青森/青森/景點/酸湯溫泉.md","lastUpdated":1766946043000}'),m={name:"日本/青森/青森/景點/酸湯溫泉.md"};function h(g,e,p,u,f,c){const l=i("PreviewLink");return s(),n("div",null,[e[0]||(e[0]=d('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/青森/青森/景點/酸湯溫泉.md for this page in Markdown format</div><h1 id="酸湯溫泉" tabindex="-1">酸湯溫泉 <a class="header-anchor" href="#酸湯溫泉" aria-label="Permalink to “酸湯溫泉”">​</a></h1><p>酸湯溫泉位於八甲田山麓，海拔約 900 公尺，是一座擁有超過 300 年歷史的古老溫泉。它以其巨大的「千人風呂」和強酸性的泉質而聞名，是日本著名的國民保養溫泉地。</p><p><img src="https://upload.wikimedia.org/wikipedia/commons/4/40/Sukayu_Onsen_01.jpg" alt="酸湯溫泉" loading="lazy"><em>圖片來源：Wikimedia Commons</em></p><h2 id="景點特色" tabindex="-1">景點特色 <a class="header-anchor" href="#景點特色" aria-label="Permalink to “景點特色”">​</a></h2><ul><li><strong>千人風呂：</strong> 溫泉旅館內著名的巨大木造浴場，面積約 248 平方公尺，全部由檜木製成，氣氛莊嚴且充滿懷舊感。</li><li><strong>強酸性泉質：</strong> 泉水含有豐富的酸性硫磺成分，對神經痛、風濕、皮膚病等有顯著療效。</li><li><strong>豪雪地帶：</strong> 這裡曾創下日本氣象觀測史上最高的積雪紀錄（566 公分），冬季時整座旅館會被厚厚的白雪覆蓋，景色壯觀。</li><li><strong>溫泉療養 (湯治)：</strong> 這裡至今仍保留著傳統的「湯治」文化，提供長期住宿的療養方案。</li></ul><h2 id="參觀資訊" tabindex="-1">參觀資訊 <a class="header-anchor" href="#參觀資訊" aria-label="Permalink to “參觀資訊”">​</a></h2><ul><li><strong>地址：</strong> 〒030-0111 青森縣青森市荒川南荒川山國有林酸湯澤 50</li><li><strong>電話：</strong> +81 17-738-6400</li><li><strong>日歸入浴時間：</strong> 07:00 - 17:30 (千人風呂)</li><li><strong>門票 (日歸)：</strong> 成人 1,000 日圓，兒童 500 日圓。</li></ul><h2 id="交通手段" tabindex="-1">交通手段 <a class="header-anchor" href="#交通手段" aria-label="Permalink to “交通手段”">​</a></h2><ul><li><strong>巴士：</strong> 從 JR 青森站或新青森站搭乘 JR 巴士（往十和田湖方向）至「酸湯溫泉」站下車，車程約 70-80 分鐘。詳見 <strong><a href="./../../交通/巴士">青森巴士指南</a></strong>。</li><li><strong>鐵路：</strong> 最近的主要車站為 <strong><a href="./../">JR 青森站</a></strong> 或 <strong><a href="./../../青森/">新青森站</a></strong>。詳見 <strong><a href="./../../交通/鐵路">青森鐵路指南</a></strong>。</li></ul><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',11)),a("ul",null,[a("li",null,[t(l,{url:"https://sukayu.jp/",placeHolderTitle:"酸湯溫泉官方網站"})]),a("li",null,[t(l,{url:"https://aomori-tourism.com/tw/spot/detail_62.html",placeHolderTitle:"AOMORI - 酸湯溫泉"})])]),e[1]||(e[1]=a("h2",{id:"溫馨提示",tabindex:"-1"},[r("溫馨提示 "),a("a",{class:"header-anchor",href:"#溫馨提示","aria-label":"Permalink to “溫馨提示”"},"​")],-1)),e[2]||(e[2]=a("ul",null,[a("li",null,[a("strong",null,"千人風呂為混浴："),r(" 雖然是混浴，但設有女性專用時段（通常為 08:00-09:00 及 20:00-21:00），且女性可穿著專用的入浴衣。")]),a("li",null,"由於泉水酸性極強，泡湯時請注意保護眼睛，且不建議將銀飾帶入池中（會變黑）。")],-1)),e[3]||(e[3]=a("h2",{id:"地圖",tabindex:"-1"},[r("地圖 "),a("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),e[4]||(e[4]=a("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3056.12!2d140.8500!3d40.6500!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2z6YW45rmv5rqr5rOJ!5e0!3m2!1szh-TW!2sjp!4v1734627200000",width:"600",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const k=o(m,[["render",h]]);export{b as __pageData,k as default};
