import{_ as t,I as l,c as n,o,a5 as i,j as a,J as s,a as d}from"./chunks/framework.BtqC3pLB.js";const b=JSON.parse('{"title":"日本 -> 靜岡 -> 景點 -> 富士山本宮淺間大社","description":"","frontmatter":{},"headers":[],"relativePath":"日本/靜岡/景點/富士山本宮淺間大社.md","filePath":"docs/日本/靜岡/景點/富士山本宮淺間大社.md","lastUpdated":1766918002000}'),h={name:"日本/靜岡/景點/富士山本宮淺間大社.md"};function m(c,e,p,f,_,u){const r=l("PreviewLink");return o(),n("div",null,[e[0]||(e[0]=i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /日本/靜岡/景點/富士山本宮淺間大社.md for this page in Markdown format</div><h1 id="富士山本宮淺間大社" tabindex="-1">富士山本宮淺間大社 <a class="header-anchor" href="#富士山本宮淺間大社" aria-label="Permalink to “富士山本宮淺間大社”">​</a></h1><p>富士山本宮淺間大社位於靜岡縣富士宮市，是日本全國約 1,300 座淺間神社的總本宮，也是富士山世界文化遺產的組成資產之一。</p><h2 id="歷史與意義" tabindex="-1">歷史與意義 <a class="header-anchor" href="#歷史與意義" aria-label="Permalink to “歷史與意義”">​</a></h2><ul><li><strong>富士山信仰中心</strong>：自古以來，富士山被視為神聖之山，而淺間大社則是為了鎮火與祭祀富士山大神（木花之佐久夜比賣命）而建。</li><li><strong>德川家康的崇敬</strong>：現在的本殿、拜殿及樓門是由德川家康於 1604 年捐資興建，本殿建築風格獨特，被稱為「淺間造」。</li></ul><h2 id="境內亮點" tabindex="-1">境內亮點 <a class="header-anchor" href="#境內亮點" aria-label="Permalink to “境內亮點”">​</a></h2><ul><li><strong>湧玉池</strong>：富士山的雪水經過濾後在此湧出，水質清澈透明。古時候登山者在出發前會在此淨身。</li><li><strong>櫻花勝地</strong>：境內種植有約 500 棵櫻花樹，春天時富士山與紅橋、櫻花交相輝映，景色極美。</li></ul><h2 id="與攀登富士山的關連" tabindex="-1">與攀登富士山的關連 <a class="header-anchor" href="#與攀登富士山的關連" aria-label="Permalink to “與攀登富士山的關連”">​</a></h2><p>富士山本宮淺間大社是<strong>富士宮線</strong>登山路線的起點象徵。許多登山者會先在此祈求登山平安後，再前往五合目開始攀登。</p><h2 id="交通資訊" tabindex="-1">交通資訊 <a class="header-anchor" href="#交通資訊" aria-label="Permalink to “交通資訊”">​</a></h2><ul><li><strong>鐵道</strong>：從 JR 身延線「富士宮站」步行約 10 分鐘。</li><li><strong>巴士</strong>：從新富士站或富士站搭乘前往富士宮方向的巴士。</li></ul><hr><h2 id="相關連結" tabindex="-1">相關連結 <a class="header-anchor" href="#相關連結" aria-label="Permalink to “相關連結”">​</a></h2>',13)),a("ul",null,[a("li",null,[s(r,{url:"http://fuji-hongu.or.jp/sengen/",placeHolderTitle:"富士山本宮淺間大社 官方網站"})])]),e[1]||(e[1]=a("h2",{id:"地圖",tabindex:"-1"},[d("地圖 "),a("a",{class:"header-anchor",href:"#地圖","aria-label":"Permalink to “地圖”"},"​")],-1)),e[2]||(e[2]=a("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3251.365345717366!2d138.6111003762696!3d35.38501257361841!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x60195e348f98993d%3A0xf63503b44b82967d!2z5a-M5aOr5bGx5pys5a6u5re66ZaT5aSn56S-!5e0!3m2!1szh-TW!2stw!4v1717855279391!5m2!1szh-TW!2stw",width:"100%",height:"450",style:{border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1))])}const x=t(h,[["render",m]]);export{b as __pageData,x as default};
